                                const a = document.createElement('a');
                                a.href = url;
                                a.download = 'live.webm';
                                a.click();
                                URL.revokeObjectURL(url);
                              }}
                            >
                              TÃ©lÃ©charger la vidÃ©o
                            </Button>
                          )}
                        </Box>
                      </Box>
                    </Grid>
                  )}
                </Grid>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={() => setActiveStep(1)} sx={{ borderRadius: 2 }}>PrÃ©cÃ©dent</Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => {
                      if (!modularTypeFields[selectedType]) {
                        console.error('Type de signalement sans formulaire :', selectedType);
                        return;
                      }
                      setActiveStep(3);
                    }}
                    sx={{ borderRadius: 2 }}
                    disabled={!modularTypeFields[selectedType]}
                  >
                    Suivant
                  </Button>
                </Box>
              </Box>
            )}
            {/* Ã‰tape 3 : Confirmation */}
            {activeStep === 3 && selectedType && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>RÃ©capitulatif et confirmation</Typography>
                <Paper variant="outlined" sx={{ p: 3, mb: 3, background: '#f9f9f9' }}>
                  <Grid container spacing={2}>
                    <Grid item xs={12}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 700, mb: 1 }}>CatÃ©gorie</Typography>
                      <Typography variant="body2">{selectedCategory?.label}</Typography>
                    </Grid>
                    <Grid item xs={12}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 700, mt: 2, mb: 1 }}>Type</Typography>
                      <Typography variant="body2">{selectedType}</Typography>
                    </Grid>
                    {/* RÃ©capitulatif dynamique selon le mode */}
                    {espacePublicTypes.includes(selectedType) && espacePublicMode === 'live' ? (
                      <>
                        <Grid item xs={12}>
                          <Typography variant="subtitle2">Mode de signalement</Typography>
                          <Typography variant="body2">Live direct</Typography>
                        </Grid>
                        <Grid item xs={12}>
                          <Typography variant="subtitle2">VidÃ©o enregistrÃ©e</Typography>
                          {liveRecordedBlob ? (
                            <a href={URL.createObjectURL(liveRecordedBlob)} target="_blank" rel="noopener noreferrer">Voir la vidÃ©o</a>
                          ) : (
                            <span style={{ color: '#aaa' }}>Aucune vidÃ©o enregistrÃ©e</span>
                          )}
                        </Grid>
                      </>
                    ) : espacePublicTypes.includes(selectedType) && espacePublicMode === 'capture' ? (
                      <>
                        <Grid item xs={12}>
                          <Typography variant="subtitle2">Mode de signalement</Typography>
                          <Typography variant="body2">Capture (photo/vidÃ©o)</Typography>
                        </Grid>
                        <Grid item xs={12}>
                          <Typography variant="subtitle2">Fichier joint</Typography>
                          {form.espacePublicCapture ? form.espacePublicCapture.name : <span style={{ color: '#aaa' }}>Aucun fichier</span>}
                        </Grid>
                      </>
                    ) : espacePublicTypes.includes(selectedType) && espacePublicMode === 'formulaire' ? (
                      <>
                        <Grid item xs={12}>
                          <Typography variant="subtitle2">Mode de signalement</Typography>
                          <Typography variant="body2">Formulaire</Typography>
                        </Grid>
                        {(modularTypeFields[selectedType] || []).map((field) => {
                          if (field.showIf && !form[field.showIf]) return null;
                          return (
                            <Grid item xs={12} sm={field.type === "textarea" ? 12 : 6} key={field.name}>
                              <Typography variant="subtitle2" sx={{ fontWeight: 700, mt: 2 }}>{field.label}</Typography>
                              <Typography variant="body2" sx={{ mb: 2 }}>
                                {field.type === "textarea" ? (
                                  <pre style={{ whiteSpace: 'pre-wrap', wordWrap: 'break-word' }}>
                                    {form[field.name]}
                                  </pre>
                                ) : field.type === "file" ? (
                                  form[field.name] ? form[field.name].name : "Aucun fichier joint"
                                ) : form[field.name]
                                }
                              </Typography>
                            </Grid>
                          );
                        })}
                      </>
                    ) : null}
                  </Grid>
                </Paper>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={() => setActiveStep(2)} sx={{ borderRadius: 2 }}>PrÃ©cÃ©dent</Button>
                  <Button
                    variant="contained"
                    color="primary"
                    type="submit"
                    sx={{ borderRadius: 2 }}
                  >
                    Confirmer l'envoi
                  </Button>
                </Box>
              </Box>
            )}
          </form>
        </Paper>
      </Container>
    </Box>
  );
}
