import React, { useState } from "react";
import { Container, Typography, Box, Button, Grid, AppBar, Toolbar } from "@mui/material";
import { Link } from "react-router-dom";

const translations = {
  fr: {
    title: "Plateforme de gestion des anomalies & fraudes",
    subtitle: "Signalez, consultez et gérez les fraudes en toute sécurité avec une interface professionnelle et intuitive.",
    login: "Connexion",
    register: "Inscription",
    start: "Signaler",
    brand: "Jub - Jubal - Jubanti"
  },
  en: {
    title: "Anomalies & Fraud Management Platform",
    subtitle: "Report, consult and manage fraud securely with a professional and intuitive interface.",
    login: "Login",
    register: "Register",
    start: "Get Started",
    brand: "Jub - Jubal - Jubanti"
  },
  wo: {
    title: "Platformu jiite ak jubal yi",
    subtitle: "Yeele, wone ak doxal jubal yi ci amna jàmm ak yoon wi.",
    login: "Jokkoo",
    register: "Inscriwaa",
    start: "Tambali",
    brand: "Jub - Jubal - Jubanti"
  }
};

export default function HomePage() {
  const [lang, setLang] = useState('fr');
  const t = translations[lang];

  // Images du carousel (adapter les chemins si besoin)
  const carouselImages = [
    '/images/diomaye.png',
    '/images/diomaye2.jpg',
    '/images/diomaye3.jpg'
  ];

  return (
    <Box sx={{ minHeight: '100vh', background: '#f8fafc', pb: 8 }}>
      {/* Header Bootstrap-like */}
      <AppBar position="static" color="default" elevation={1} sx={{ mb: 4 }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <img src="/images/jub.png" alt="Logo" style={{ width: 50, marginRight: 16 }} />
            <Typography variant="h6" sx={{ fontWeight: 700, color: '#343a40', letterSpacing: 1 }}>
              {t.brand}
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Button component={Link} to="/login" variant="outlined" sx={{ color: '#343a40', borderColor: '#343a40', fontWeight: 600, mx: 1, borderRadius: '50px', px: 3, py: 1 }}>
              {t.login}
            </Button>
            <Button component={Link} to="/register" variant="contained" sx={{ background: '#007bff', color: '#fff', fontWeight: 600, borderRadius: '50px', px: 3, py: 1, boxShadow: 2 }}>
              {t.register}
            </Button>
            <label htmlFor="lang-select" style={{ marginLeft: 16, fontWeight: 600, color: '#343a40' }}>🌐</label>
            <select
              id="lang-select"
              value={lang}
              onChange={e => setLang(e.target.value)}
              style={{ marginLeft: 8, padding: '6px 12px', borderRadius: 8, border: '1px solid #343a40', fontWeight: 600, color: '#343a40', background: '#fff', outline: 'none', cursor: 'pointer' }}
            >
              <option value="fr">Français</option>
              <option value="en">English</option>
              <option value="wo">Wolof</option>
            </select>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Carousel Bootstrap-like */}
      <Container maxWidth="md" sx={{ mt: 4, mb: 6 }}>
        <Box sx={{ position: 'relative', width: '100%', height: { xs: 220, sm: 350, md: 420 }, overflow: 'hidden', borderRadius: 4, boxShadow: 3, mb: 4 }}>
          <CarouselBootstrap images={carouselImages} />
        </Box>
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography variant="h2" sx={{ fontWeight: 700, color: '#343a40', mb: 2, lineHeight: 1.1, fontSize: { xs: 28, sm: 36, md: 48 } }}>
            {t.title}
          </Typography>
          <Typography variant="h5" sx={{ color: '#495057', mb: 4 }}>
            {t.subtitle}
          </Typography>
          <Button component={Link} to="/login" size="large" variant="contained" sx={{ background: '#007bff', color: '#fff', fontWeight: 700, borderRadius: '50px', px: 5, py: 1.5, boxShadow: 3 }}>
            {t.start}
          </Button>
        </Box>
      </Container>
    </Box>
  );
}

// Composant Carousel façon Bootstrap (auto, flèches, points)
function CarouselBootstrap({ images }) {
  const [index, setIndex] = useState(0);
  const next = () => setIndex((i) => (i + 1) % images.length);
  const prev = () => setIndex((i) => (i - 1 + images.length) % images.length);
  React.useEffect(() => {
    const timer = setTimeout(next, 4000);
    return () => clearTimeout(timer);
  }, [index, images.length]);
  return (
    <Box sx={{ width: '100%', height: '100%', position: 'relative' }}>
      {images.map((img, i) => (
        <Box key={img} sx={{
          display: i === index ? 'block' : 'none',
          width: '100%',
          height: '100%',
          position: 'absolute',
          top: 0,
          left: 0,
          transition: 'opacity 0.7s',
        }}>
          <img src={img} alt={"carousel-" + i} style={{ width: '100%', height: '100%', objectFit: 'cover', borderRadius: 8 }} />
        </Box>
      ))}
      {/* Flèches */}
      <Button onClick={prev} sx={{ position: 'absolute', top: '50%', left: 8, transform: 'translateY(-50%)', minWidth: 0, p: 1, bgcolor: 'rgba(0,0,0,0.3)', color: '#fff', borderRadius: '50%' }}>
        &#8592;
      </Button>
      <Button onClick={next} sx={{ position: 'absolute', top: '50%', right: 8, transform: 'translateY(-50%)', minWidth: 0, p: 1, bgcolor: 'rgba(0,0,0,0.3)', color: '#fff', borderRadius: '50%' }}>
        &#8594;
      </Button>
      {/* Points */}
      <Box sx={{ position: 'absolute', bottom: 12, left: '50%', transform: 'translateX(-50%)', display: 'flex', gap: 1 }}>
        {images.map((_, i) => (
          <Box key={i} onClick={() => setIndex(i)} sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: i === index ? '#007bff' : '#dee2e6', cursor: 'pointer', border: '1px solid #007bff' }} />
        ))}
      </Box>
    </Box>
  );
}
