    if (activeStep === 1 && !selectedType) {
      setError("Veuillez sÃ©lectionner un type de signalement.");
      return;
    }
    if (activeStep === 2 && (!form.nom || !form.prenom || !form.email)) {
      setError("Veuillez remplir tous les champs personnels obligatoires.");
      return;
    }
    if (activeStep === 3 && !form.description) {
      setError("Merci de dÃ©crire prÃ©cisÃ©ment le signalement.");
      return;
    }
    setError("");
    setActiveStep((s) => s + 1);
  };

  const handleBack = () => {
    setError("");
    setActiveStep((s) => s - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess("Votre signalement a bien Ã©tÃ© transmis. Merci pour votre vigilance.");
    setActiveStep(steps.length - 1);
  };

  // VÃ©rification de la complÃ©tude des formulaires pour chaque type
  const missingTypes = categories.flatMap(cat =>
    cat.types.filter(type => !modularTypeFields[type])
  );

  if (missingTypes.length > 0) {
    console.error("Types de signalement sans formulaire obligatoire :", missingTypes);
  }

  return (
    <Box sx={{ minHeight: '100vh', background: '#fff', py: 6, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
