          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mb: 4, width: '100%' }}>
            <img src="/images/jub.png" alt="Logo SÃ©nÃ©gal" style={{ width: 60, marginRight: 32, borderRadius: '50%' }} />
            <Typography variant="h3" sx={{ fontWeight: 800, color: '#1a237e', whiteSpace: 'nowrap' }}>
              Je signale
            </Typography>
          </Box>
          <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
            <Step><StepLabel>CatÃ©gorie</StepLabel></Step>
            <Step><StepLabel>Type</StepLabel></Step>
            <Step><StepLabel>Informations</StepLabel></Step>
            <Step><StepLabel>Confirmation</StepLabel></Step>
          </Stepper>
          {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
          {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
          <form onSubmit={handleSubmit}>
            {/* Ã‰tape 0 : CatÃ©gorie */}
            {activeStep === 0 && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>Choisissez une catÃ©gorie de signalement :</Typography>
                <Grid container spacing={2}>
                  {categories.map((cat) => (
                    <Grid item xs={12} sm={6} md={4} key={cat.label}>
                      <Paper
                        elevation={selectedCategory === cat ? 8 : 2}
                        sx={{
                          p: 3,
                          textAlign: 'center',
                          cursor: 'pointer',
                          border: selectedCategory === cat ? '2px solid #00008b' : '1px solid #c3cfe2',
                          background: selectedCategory === cat ? '#00008b' : '#fff',
                          color: selectedCategory === cat ? '#fff' : '#1a237e',
                          transition: 'all 0.3s cubic-bezier(.4,2,.6,1)',
                          boxShadow: selectedCategory === cat ? '0 8px 32px 0 rgba(0,0,139,0.18)' : '0 2px 8px 0 rgba(26,35,126,0.07)',
                          borderRadius: 4,
                          minHeight: 170,
                          display: 'flex',
                          flexDirection: 'column',
                          alignItems: 'center',
                          justifyContent: 'center',
                          position: 'relative',
                          overflow: 'hidden',
                          '&:hover': {
                            boxShadow: '0 12px 36px 0 rgba(0,0,139,0.22)',
                            transform: 'translateY(-4px) scale(1.03)',
                            background: selectedCategory === cat ? '#00008b' : '#1a237e',
                            color: '#fff',
                            border: '2px solid #00008b',
                          },
                          // Animation dÃ©corative
                          '::before': {
                            content: '""',
                            position: 'absolute',
                            top: -30,
                            left: -30,
                            width: 60,
                            height: 60,
                            background: 'rgba(25, 118, 210, 0.08)',
                            borderRadius: '50%',
                            zIndex: 0,
                            animation: 'pulseCat 2.5s infinite cubic-bezier(.4,2,.6,1)'
                          },
                        }}
                        onClick={() => { setSelectedCategory(cat); setSelectedType(""); }}
                      >
                        <Box sx={{ position: 'relative', zIndex: 1 }}>
                          <Typography variant="h3" sx={{ mb: 1 }}>{cat.icon}</Typography>
                          <Typography variant="subtitle1" sx={{ fontWeight: 700 }}>{cat.label}</Typography>
                        </Box>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
                {/* Afficher le bouton Suivant uniquement si une catÃ©gorie est sÃ©lectionnÃ©e */}
                <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 3 }}>
                  {selectedCategory && (
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => setActiveStep(1)}
                      sx={{ borderRadius: 2 }}
                    >
                      Suivant
                    </Button>
                  )}
                </Box>
              </Box>
            )}
            {/* Ã‰tape 1 : Type */}
            {activeStep === 1 && selectedCategory && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>SÃ©lectionnez le type de signalement :</Typography>
                <Grid container spacing={2}>
                  {selectedCategory.types.map((type) => (
                    <Grid item xs={12} sm={6} md={6} key={type}>
                      <Paper elevation={selectedType === type ? 8 : 2} sx={{ p: 3, display: 'flex', alignItems: 'center', cursor: 'pointer', border: selectedType === type ? '2px solid #1976d2' : '1px solid #c3cfe2', background: selectedType === type ? '#e3eafc' : '#fff', transition: 'all 0.2s' }} onClick={() => setSelectedType(type)}>
                        <Radio
                          checked={selectedType === type}
                          onChange={() => setSelectedType(type)}
                          color="primary"
                          sx={{ mr: 2 }}
                        />
                        <Typography variant="body1">{type}</Typography>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={() => { setActiveStep(0); setSelectedCategory(null); setSelectedType(""); }} sx={{ borderRadius: 2 }}>PrÃ©cÃ©dent</Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => selectedType && setActiveStep(2)}
                    sx={{ borderRadius: 2 }}
                    disabled={!selectedType}
                  >
                    Suivant
                  </Button>
                </Box>
              </Box>
            )}
            {/* Ã‰tape 2 : Informations (formulaire dynamique selon le type, obligatoire) */}
            {activeStep === 2 && selectedType && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>Informations sur le signalement</Typography>
                {/* Si type = espace public, proposer le choix du mode */}
                {espacePublicTypes.includes(selectedType) && (
                  <Box sx={{ mb: 3 }}>
                    <Typography variant="subtitle1" sx={{ mb: 1 }}>Mode de signalement :</Typography>
                    <RadioGroup
                      row
                      value={espacePublicMode}
                      onChange={e => setEspacePublicMode(e.target.value)}
                    >
                      <FormControlLabel value="formulaire" control={<Radio color="primary" />} label="Formulaire" />
                      <FormControlLabel value="capture" control={<Radio color="primary" />} label="Capture (photo/vidÃ©o)" />
                      <FormControlLabel value="live" control={<Radio color="primary" />} label="Live direct" />
                    </RadioGroup>
                  </Box>
                )}
                <Grid container spacing={2}>
                  {/* Bloc formulaire classique */}
                  {(!espacePublicTypes.includes(selectedType) || espacePublicMode === 'formulaire') &&
                    (modularTypeFields[selectedType] || []).map((field) => {
                      if (field.showIf && !form[field.showIf]) return null;
                      return (
                        <Grid item xs={12} sm={field.type === "textarea" ? 12 : 6} key={field.name}>
                          {field.type === "textarea" ? (
                            <TextField
                              label={field.label}
                              name={field.name}
                              value={form[field.name] || ""}
                              onChange={handleChange}
                              fullWidth
                              required={field.required}
                              multiline
                              rows={4}
                            />
                          ) : field.type === "file" ? (
                            <Button
                              variant="outlined"
                              component="label"
                              fullWidth
                            >
                              {field.label}
                              <input
                                type="file"
                                name={field.name}
                                hidden
                                onChange={e => setForm({ ...form, [field.name]: e.target.files[0] })}
                              />
                            </Button>
                          ) : (
                            <TextField
                              label={field.label}
                              name={field.name}
                              value={form[field.name] || ""}
                              onChange={handleChange}
                              fullWidth
                              required={field.required}
                              type={field.type || "text"}
                            />
                          )}
                        </Grid>
                      );
                    })}
                  {/* Bloc capture uniquement */}
                  {espacePublicTypes.includes(selectedType) && espacePublicMode === 'capture' && (
                    <Grid item xs={12}>
                      <Button
                        variant="outlined"
                        component="label"
                        fullWidth
                      >
                        Joindre une capture (photo ou vidÃ©o)
                        <input
                          type="file"
                          name="espacePublicCapture"
                          accept="image/*,video/*"
                          hidden
                          onChange={e => setForm({ ...form, espacePublicCapture: e.target.files[0] })}
                        />
                      </Button>
                    </Grid>
                  )}
                  {/* Bloc live direct uniquement */}
                  {espacePublicTypes.includes(selectedType) && espacePublicMode === 'live' && (
                    <Grid item xs={12} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: 220 }}>
                      <Typography variant="subtitle1" sx={{ mb: 2, textAlign: 'center' }}>
                        Lancez votre live directement depuis le site
                      </Typography>
                      <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 2 }}>
                        {/* IntÃ©gration d'un composant vidÃ©o live natif (getUserMedia) */}
                        <video id="liveVideo" autoPlay playsInline muted style={{ width: 320, height: 200, borderRadius: 12, background: '#eee', border: '2px solid #1976d2' }} />
                        <Button
                          variant="contained"
                          color="primary"
                          sx={{ borderRadius: 2, minWidth: 180 }}
                          onClick={async () => {
                            try {
                              const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
                              const video = document.getElementById('liveVideo');
                              if (video) video.srcObject = stream;
                            } catch (err) {
                              alert('Impossible d\'accÃ©der Ã  la camÃ©ra/microphone.');
                            }
                          }}
                        >
                          DÃ©marrer le live
                        </Button>
                      </Box>
                    </Grid>
                  )}
                </Grid>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={() => setActiveStep(1)} sx={{ borderRadius: 2 }}>PrÃ©cÃ©dent</Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => {
                      if (!modularTypeFields[selectedType]) {
                        console.error('Type de signalement sans formulaire :', selectedType);
                        return;
                      }
                      setActiveStep(3);
                    }}
                    sx={{ borderRadius: 2 }}
                    disabled={!modularTypeFields[selectedType]}
                  >
                    Suivant
                  </Button>
                </Box>
              </Box>
            )}
            {/* Ã‰tape 3 : Confirmation */}
            {activeStep === 3 && selectedType && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>RÃ©capitulatif et confirmation</Typography>
                <Paper variant="outlined" sx={{ p: 3, mb: 3, background: '#f9f9f9' }}>
                  <Grid container spacing={2}>
                    <Grid item xs={12}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 700, mb: 1 }}>CatÃ©gorie</Typography>
                      <Typography variant="body2">{selectedCategory?.label}</Typography>
                    </Grid>
                    <Grid item xs={12}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 700, mt: 2, mb: 1 }}>Type</Typography>
                      <Typography variant="body2">{selectedType}</Typography>
                    </Grid>
                    {(modularTypeFields[selectedType] || []).map((field) => {
                      if (field.showIf && !form[field.showIf]) return null;
                      let value = form[field.name];
                      if (field.type === "checkbox") value = value ? "Oui" : "Non";
                      if (field.type === "file" && value) value = value.name;
                      if (!value) value = <span style={{ color: '#aaa' }}>Non renseignÃ©</span>;
                      return (
                        <React.Fragment key={field.name}>
                          <Grid item xs={6} sm={4}>
                            <Typography variant="subtitle2">{field.label}</Typography>
                          </Grid>
                          <Grid item xs={6} sm={8}>
                            <Typography variant="body2">{value}</Typography>
                          </Grid>
                        </React.Fragment>
                      );
                    })}
                  </Grid>
                </Paper>
                <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 2 }}>
                  <Button variant="outlined" onClick={() => window.print()} sx={{ borderRadius: 2 }}>Imprimer</Button>
                  <Button variant="outlined" onClick={() => {
                    const data = {
                      CatÃ©gorie: selectedCategory?.label,
                      Type: selectedType,
                      ...((modularTypeFields[selectedType] || []).reduce((acc, field) => {
                        if (field.showIf && !form[field.showIf]) return acc;
                        acc[field.label] = form[field.name] || '';
                        return acc;
                      }, {}))
                    };
                    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
                    const url = URL.createObjectURL(blob);
                    const a = document.createElement('a');
                    a.href = url;
                    a.download = 'signalement.json';
                    a.click();
                    URL.revokeObjectURL(url);
                  }} sx={{ borderRadius: 2 }}>TÃ©lÃ©charger</Button>
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Button variant="outlined" onClick={() => setActiveStep(2)} sx={{ borderRadius: 2 }}>PrÃ©cÃ©dent</Button>
                  <Button variant="contained" color="primary" type="submit" sx={{ borderRadius: 2 }}>Envoyer</Button>
                </Box>
              </Box>
            )}
            {/* Ã‰tape 4 : Merci */}
            {activeStep === 4 && (
              <Box sx={{ textAlign: 'center', py: 6 }}>
                <Typography variant="h4" color="success.main" sx={{ mb: 2 }}>Merci pour votre signalement !</Typography>
                <Typography variant="body1">Votre demande a bien Ã©tÃ© prise en compte. Nos Ã©quipes vont l'Ã©tudier dans les plus brefs dÃ©lais.</Typography>
              </Box>
            )}
          </form>
        </Paper>
      </Container>
    </Box>
  );
}
<style>
{`
@keyframes pulseCat {
  0% { opacity: 0.5; transform: scale(1); }
  50% { opacity: 0.9; transform: scale(1.12); }
  100% { opacity: 0.5; transform: scale(1); }
}
`}
</style>
