import React, { useState } from 'react';
import { Box, Container, Paper, Typography, Grid, Button, Alert, TextField, Stepper, Step, StepLabel, MobileStepper, Card, CardContent } from '@mui/material';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import PersonIcon from '@mui/icons-material/Person';
import GavelIcon from '@mui/icons-material/Gavel';
import PublicIcon from '@mui/icons-material/Public';
import ConstructionIcon from '@mui/icons-material/Construction';
import StorefrontIcon from '@mui/icons-material/Storefront';
import PetsIcon from '@mui/icons-material/Pets';
import SecurityIcon from '@mui/icons-material/Security';

// Palette bleu et blanc uniquement
const senegalColors = {
  blue: '#1976d2', // Bleu principal
  darkBlue: '#00008b', // Bleu foncé
  lightBlue: '#e3e8ff',
  white: '#fff',
};

// Nouvelle structure modulaire des catégories, types et formulaires (issue du JSON fourni)
const categories = [
  {
    id: 'administration',
    label: 'Administration / Services publics',
    types: [
      {
        id: 'abus_pouvoir',
        label: 'Abus de pouvoir ou comportement inapproprié',
        form: [
          { name: 'service_concerne', label: 'Service concerné', type: 'text' },
          { name: 'description', label: 'Description', type: 'textarea' },
          { name: 'preuve', label: 'Preuve', type: 'file' },
          { name: 'date', label: 'Date', type: 'date' }
        ]
      },
      {
        id: 'discrimination_administrative',
        label: 'Discrimination ou traitement inégal',
        form: [
          { name: 'organisme', label: 'Organisme', type: 'text' },
          { name: 'nature_discrimination', label: 'Nature de la discrimination', type: 'text' },
          { name: 'preuve', label: 'Preuve', type: 'file' },
          { name: 'date', label: 'Date', type: 'date' }
        ]
      }
    ]
  },
  {
    id: 'reseaux_sociaux',
    label: 'Réseaux sociaux',
    types: [
      {
        id: 'intimidation',
        label: 'Intimidation / harcèlement en ligne',
        form: [
          { name: 'plateforme', label: 'Plateforme', type: 'select', options: ['Instagram', 'Facebook', 'Snapchat', 'TikTok', 'Twitter'], required: true },
          { name: 'compte_auteur', label: 'Compte auteur', type: 'text' },
          { name: 'description', label: 'Description', type: 'textarea' },
          { name: 'preuve', label: 'Preuve', type: 'file', multiple: true },
          { name: 'date_faits', label: 'Date des faits', type: 'date' }
        ]
      },
      {
        id: 'revenge_porn',
        label: 'Diffusion non consentie de contenu intime',
        form: [
          { name: 'plateforme', label: 'Plateforme', type: 'text' },
          { name: 'lien_contenu', label: 'Lien du contenu', type: 'url' },
          { name: 'preuve', label: 'Preuve', type: 'file' },
          { name: 'description', label: 'Description', type: 'textarea' }
        ]
      }
    ]
  },
  {
    id: 'harcelement',
    label: 'Harcèlement (toutes formes)',
    types: [
      {
        id: 'travail',
        label: 'Harcèlement au travail',
        form: [
          { name: 'employeur', label: 'Employeur', type: 'text' },
          { name: 'description', label: 'Description', type: 'textarea' },
          { name: 'preuve', label: 'Preuve', type: 'file' },
          { name: 'date', label: 'Date', type: 'date' }
        ]
      },
      {
        id: 'scolaire',
        label: 'Harcèlement scolaire',
        form: [
          { name: 'etablissement', label: 'Établissement', type: 'text' },
          { name: 'description', label: 'Description', type: 'textarea' },
          { name: 'preuve', label: 'Preuve', type: 'file' },
          { name: 'date', label: 'Date', type: 'date' }
        ]
      }
    ]
  },
  {
    id: 'commerce_complementaire',
    label: 'Commerce complémentaire',
    types: [
      {
        id: 'non_remboursement',
        label: 'Non remboursement / retour refusé',
        form: [
          { name: 'commerce', label: 'Commerce', type: 'text' },
          { name: 'produit_service', label: 'Produit ou service', type: 'text' },
          { name: 'preuve', label: 'Preuve', type: 'file' },
          { name: 'date', label: 'Date', type: 'date' }
        ]
      },
      {
        id: 'publicite_mensongere',
        label: 'Publicité mensongère',
        form: [
          { name: 'support', label: 'Support', type: 'text' },
          { name: 'produit_service', label: 'Produit ou service', type: 'text' },
          { name: 'preuve', label: 'Preuve', type: 'file' },
          { name: 'description', label: 'Description', type: 'textarea' }
        ]
      }
    ]
  }
];

const steps = ['Catégorie', 'Type', 'Informations', 'Confirmation'];

export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState(null);
  const [selectedTypes, setSelectedTypes] = useState([]);
  const [form, setForm] = useState({});
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Sélection catégorie
  const handleSelectCategory = (cat) => {
    setSelectedCategory(cat);
    setSelectedType(null);
    setSelectedTypes([]);
    setForm({});
    setActiveStep(1);
    setError("");
  };
  // Sélection type
  const handleSelectType = (type) => {
    setSelectedType(type);
    setForm({});
    setActiveStep(2);
    setError("");
  };
  // Gestion checkbox type (sélection multiple)
  const handleTypeCheckbox = (type) => {
    if (selectedTypes.some(t => t.id === type.id)) {
      setSelectedTypes(selectedTypes.filter(t => t.id !== type.id));
    } else {
      setSelectedTypes([...selectedTypes, type]);
    }
  };
  // Retour
  const handleBack = () => {
    if (activeStep === 1) {
      setSelectedCategory(null);
      setActiveStep(0);
    } else if (activeStep === 2) {
      setSelectedType(null);
      setActiveStep(1);
    } else if (activeStep === 3) {
      setActiveStep(2);
    }
    setError("");
  };
  // Formulaire dynamique
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  // Validation étape
  const validateStep = () => {
    if (activeStep === 0 && !selectedCategory) {
      setError('Veuillez choisir une catégorie.');
      return false;
    }
    if (activeStep === 1 && selectedTypes.length === 0) {
      setError('Veuillez choisir au moins un type de signalement.');
      return false;
    }
    if (activeStep === 2 && selectedType) {
      for (const field of selectedType.form) {
        if (field.required && !form[field.name]) {
          setError(`Veuillez renseigner le champ : ${field.label}`);
          return false;
        }
      }
    }
    setError("");
    return true;
  };
  // Suivant
  const handleNext = () => {
    if (!validateStep()) return;
    setActiveStep((prev) => prev + 1);
  };
  // Soumission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateStep()) return;
    setSuccess("Signalement transmis ! Merci pour votre vigilance.");
    setActiveStep(3);
  };

  // Affichage dynamique par étape
  const renderStepContent = () => {
    if (activeStep === 0) {
      // Catégories : blocs responsives, s'adaptent à l'espace disponible avec espace entre les blocs
      return (
        <Box sx={{ width: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: 400 }}>
          <Box sx={{ width: '100%', maxWidth: 1400, px: { xs: 1.5, sm: 3, md: 4 }, mx: 'auto' }}>
            <Typography variant="h6" sx={{ mb: 3, textAlign: 'center', color: senegalColors.green, fontWeight: 700, letterSpacing: 1, textShadow: '0 2px 8px ' + senegalColors.lightYellow }}>Choisissez une catégorie</Typography>
            <Grid container spacing={{ xs: 2, sm: 3, md: 4, lg: 5 }} justifyContent="center" alignItems="stretch" wrap="wrap">
              {categories.map((cat, idx) => (
                <Grid item xs={12} sm={6} md={4} key={cat.id} sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  height: '100%',
                  minWidth: 0,
                  boxSizing: 'border-box',
                  mb: { xs: 2, sm: 2, md: 0 },
                  // Suppression des paddings latéraux sur mobile/tablette
                  pr: { md: idx % 3 !== 2 ? 2 : 0 },
                  pl: { md: idx % 3 !== 0 ? 2 : 0 },
                  px: { xs: 0, sm: 0, md: 0 },
                  width: '100%',
                  maxWidth: '100%',
                }}>
                  <Card
                    sx={{
                      cursor: 'pointer',
                      border: 'none',
                      borderRadius: 4,
                      boxShadow: selectedCategory?.id === cat.id ? 8 : 2,
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                      minHeight: { xs: 120, sm: 150, md: 180, lg: 200 },
                      minWidth: 0,
                      width: '100%',
                      boxSizing: 'border-box',
                      p: { xs: 1, sm: 2, md: 2.5 },
                      background: selectedCategory?.id === cat.id
                        ? 'linear-gradient(120deg, ' + senegalColors.lightBlue + ' 0%, ' + senegalColors.blue + ' 100%)'
                        : idx % 2 === 0
                          ? 'linear-gradient(120deg, ' + senegalColors.white + ' 0%, ' + senegalColors.lightBlue + ' 100%)'
                          : 'linear-gradient(120deg, #f3f4f6 0%, ' + senegalColors.white + ' 100%)',
                      transition: 'all 0.2s cubic-bezier(.4,2,.6,1)',
                      overflow: 'hidden',
                      position: 'relative',
                      '&:hover': {
                        boxShadow: 12,
                        border: 'none',
                        background: 'linear-gradient(120deg, ' + senegalColors.darkBlue + ' 0%, ' + senegalColors.blue + ' 100%)',
                        transform: 'scale(1.03)',
                        '& .MuiTypography-root, & .MuiSvgIcon-root': {
                          color: senegalColors.white,
                          transition: 'color 0.2s',
                        },
                      },
                      ...(selectedCategory?.id === cat.id && {
                        transform: 'scale(1.02)',
                      })
                    }}
                    onClick={() => handleSelectCategory(cat)}
                  >
                    <Box sx={{ mb: 1, transition: 'color 0.2s', color: selectedCategory?.id === cat.id ? senegalColors.white : senegalColors.blue, animation: selectedCategory?.id === cat.id ? 'bounce 0.7s' : 'none' }}>
                      {React.cloneElement(cat.icon, { sx: { fontSize: 48, color: selectedCategory?.id === cat.id ? senegalColors.white : senegalColors.blue, transition: 'color 0.2s' } })}
                    </Box>
                    <CardContent sx={{ p: 0, width: '100%' }}>
                      <Typography variant="h6" sx={{ fontWeight: 700, color: selectedCategory?.id === cat.id ? senegalColors.white : senegalColors.blue, mb: 1, textAlign: 'center', transition: 'color 0.2s', whiteSpace: 'normal', overflow: 'hidden', textOverflow: 'ellipsis', fontSize: { xs: 15, sm: 17, md: 19 }, letterSpacing: 0.3 }}>{cat.label}</Typography>
                      <Box sx={{ width: '100%', height: '1.5px', background: senegalColors.blue, my: 1, borderRadius: 1 }} />
                      <Typography variant="body2" sx={{ color: senegalColors.blue, textAlign: 'center', minHeight: 28, maxHeight: 36, overflow: 'hidden', textOverflow: 'ellipsis', fontSize: { xs: 12, sm: 13, md: 14 }, opacity: 0.9 }}>{cat.description}</Typography>
                    </CardContent>
                    {selectedCategory?.id === cat.id && (
                      <Box sx={{ position: 'absolute', right: 16, top: 16, animation: 'fadeIn 0.5s' }}>
                        <span style={{ color: senegalColors.white, fontWeight: 700, fontSize: 20 }}>✓</span>
                      </Box>
                    )}
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        </Box>
      );
    }
    if (activeStep === 1 && selectedCategory) {
      // Types de la catégorie : liste à puce, choix unique (radio) avec style et animation
      return (
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: 400 }}>
          <Box sx={{ width: '100%', maxWidth: 700, mx: 'auto', background: 'linear-gradient(120deg, #e3e8ff 0%, #f8fafc 100%)', borderRadius: 4, boxShadow: 6, p: 4, transition: 'box-shadow 0.3s' }}>
            <Typography variant="h6" sx={{ mb: 3, textAlign: 'center', color: '#1a237e', fontWeight: 700, letterSpacing: 1, textShadow: '0 2px 8px #e3e8ff' }}>Quel type de signalement&nbsp;?</Typography>
            <Box component="ul" sx={{ listStyle: 'none', pl: 0, mb: 3 }}>
              {selectedCategory.types.map((type) => (
                <Box
                  component="li"
                  key={type.id}
                  sx={{
                    mb: 2,
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    borderRadius: 3,
                    boxShadow: selectedType?.id === type.id ? 8 : 2,
                    background:
                      selectedType?.id === type.id
                        ? 'linear-gradient(90deg, #00008b 0%, #1976d2 100%)' // fond bleu foncé si sélectionné
                        : '#fff',
                    border: selectedType?.id === type.id ? '2px solid #00008b' : '2px solid #e0e0e0',
                    p: 2,
                    transition: 'all 0.25s cubic-bezier(.4,2,.6,1)',
                    position: 'relative',
                    overflow: 'hidden',
                    color: selectedType?.id === type.id ? '#fff' : '#000', // texte blanc si sélectionné
                    '& .MuiTypography-root, & .MuiSvgIcon-root': {
                      color: selectedType?.id === type.id ? '#fff' : undefined,
                      transition: 'color 0.2s',
                    },
                    '&:hover': {
                      boxShadow: 12,
                      background: 'linear-gradient(90deg, #00008b 0%, #1976d2 100%)' // bleu foncé au survol
                      ,
                      border: '2px solid #1976d2',
                      transform: 'scale(1.02)',
                      color: '#fff',
                      '& .MuiTypography-root, & .MuiSvgIcon-root': {
                        color: '#fff',
                        transition: 'color 0.2s',
                      },
                    },
                    ...(selectedType?.id === type.id && {
                      color: '#00008b',
                    })
                  }}
                  onClick={() => setSelectedType(type)}
                >
                  <Box sx={{ mr: 2, display: 'flex', alignItems: 'center', position: 'relative' }}>
                    <input
                      type="radio"
                      name="type-signalement"
                      checked={selectedType?.id === type.id}
                      onChange={() => setSelectedType(type)}
                      style={{ marginRight: 0, width: 22, height: 22, accentColor: '#00008b', transition: 'box-shadow 0.2s' }}
                    />
                    {selectedType?.id === type.id && (
                      <Box sx={{
                        position: 'absolute',
                        left: 0,
                        top: 0,
                        width: 22,
                        height: 22,
                        borderRadius: '50%',
                        boxShadow: '0 0 0 6px #c7d2fe',
                        pointerEvents: 'none',
                        animation: 'pulse 1.2s infinite',
                        '@keyframes pulse': {
                          '0%': { boxShadow: '0 0 0 0 #c7d2fe' },
                          '70%': { boxShadow: '0 0 0 8px #e0e7ff' },
                          '100%': { boxShadow: '0 0 0 0 #c7d2fe' },
                        },
                      }} />
                    )}
                  </Box>
                  <Box sx={{ flex: 1 }}>
                    <Typography variant="body1" sx={{ fontWeight: 700, color: selectedType?.id === type.id ? '#00008b' : '#1976d2', display: 'inline', fontSize: 18, letterSpacing: 0.2, transition: 'color 0.2s' }}>{type.label}</Typography>
                    <Typography variant="body2" sx={{ color: '#374151', ml: 1, display: 'inline', fontSize: 15, opacity: 0.85 }}>{type.description}</Typography>
                  </Box>
                  {selectedType?.id === type.id && (
                    <Box sx={{ position: 'absolute', right: 16, top: 16, animation: 'fadeIn 0.5s' }}>
                      <span style={{ color: '#00008b', fontWeight: 700, fontSize: 18 }}>✓</span>
                    </Box>
                  )}
                </Box>
              ))}
            </Box>
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
              <Button onClick={handleBack} sx={{ borderRadius: 2, mr: 2, fontWeight: 600, px: 3, background: senegalColors.lightBlue, color: senegalColors.blue, boxShadow: 2, '&:hover': { background: senegalColors.blue, color: senegalColors.white } }}>Retour</Button>
              <Button
                variant="contained"
                onClick={() => selectedType && setActiveStep(2)}
                sx={{ borderRadius: 2, fontWeight: 700, px: 5, boxShadow: 4, background: selectedType ? 'linear-gradient(90deg, ' + senegalColors.blue + ' 0%, ' + senegalColors.darkBlue + ' 100%)' : '#ccc', color: selectedType ? senegalColors.white : '#888', transition: 'background 0.2s', '&:hover': { background: selectedType ? senegalColors.darkBlue : '#ccc', color: senegalColors.white } }}
                disabled={!selectedType}
              >
                Valider
              </Button>
            </Box>
          </Box>
        </Box>
      );
    }
    if (activeStep === 2 && selectedType) {
      // Formulaire dynamique
      return (
        <Box component="form" onSubmit={handleSubmit}>
          <Typography variant="h6" sx={{ mb: 2, textAlign: 'center' }}>Formulaire&nbsp;: {selectedType.label}</Typography>
          <Grid container spacing={2}>
            {selectedType.form.map((field) => (
              <Grid item xs={12} sm={6} key={field.name}>
                {field.type === 'textarea' ? (
                  <TextField
                    label={field.label}
                    name={field.name}
                    value={form[field.name] || ''}
                    onChange={handleChange}
                    fullWidth
                    required={field.required}
                    multiline
                    rows={4}
                  />
                ) : (
                  <TextField
                    label={field.label}
                    name={field.name}
                    value={form[field.name] || ''}
                    onChange={handleChange}
                    fullWidth
                    required={field.required}
                    type={field.type}
                  />
                )}
              </Grid>
            ))}
          </Grid>
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
            <Button onClick={handleBack} sx={{ borderRadius: 2, mr: 2, fontWeight: 600, background: senegalColors.lightBlue, color: senegalColors.blue, '&:hover': { background: senegalColors.blue, color: senegalColors.white } }}>Retour</Button>
            <Button variant="contained" type="submit" sx={{ borderRadius: 2, fontWeight: 600, px: 4, background: 'linear-gradient(90deg, ' + senegalColors.blue + ' 0%, ' + senegalColors.darkBlue + ' 100%)', color: senegalColors.white, '&:hover': { background: senegalColors.darkBlue, color: senegalColors.white } }}>
              Suivant
            </Button>
          </Box>
        </Box>
      );
    }
    if (activeStep === 3 && selectedType) {
      // Récapitulatif
      return (
        <Box>
          <Typography variant="h6" sx={{ mb: 2, textAlign: 'center' }}>Récapitulatif du signalement</Typography>
          <Paper elevation={2} sx={{ p: 3, borderRadius: 3, mb: 2, maxWidth: 600, mx: 'auto' }}>
            <Typography variant="subtitle1"><b>Catégorie :</b> {selectedCategory.label}</Typography>
            <Typography variant="subtitle1"><b>Type :</b> {selectedType.label}</Typography>
            {selectedType.form.map((field) => (
              <Typography key={field.name} variant="subtitle1"><b>{field.label} :</b> {form[field.name]}</Typography>
            ))}
          </Paper>
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mt: 2 }}>
            <Button variant="outlined" sx={{ borderRadius: 2, fontWeight: 600, color: senegalColors.blue, borderColor: senegalColors.blue, '&:hover': { background: senegalColors.blue, color: senegalColors.white, borderColor: senegalColors.blue } }} onClick={() => window.print()}>
              Imprimer
            </Button>
            <Button variant="contained" sx={{ borderRadius: 2, fontWeight: 600, background: 'linear-gradient(90deg, ' + senegalColors.lightBlue + ' 0%, ' + senegalColors.blue + ' 100%)', color: senegalColors.white, '&:hover': { background: senegalColors.darkBlue } }} onClick={() => {
              const blob = new Blob([
                `Catégorie: ${selectedCategory.label}\nType: ${selectedType.label}\n` + selectedType.form.map(f => `${f.label}: ${form[f.name]}`).join('\n')
              ], { type: 'text/plain' });
              const url = URL.createObjectURL(blob);
              const a = document.createElement('a');
              a.href = url;
              a.download = 'signalement.txt';
              a.click();
              URL.revokeObjectURL(url);
            }}>
              Télécharger
            </Button>
          </Box>
        </Box>
      );
    }
    return null;
  };

  // Ajout d'un fond général élégant et d'un header professionnel
  return (
    <Box sx={{
      minHeight: '100vh',
      background: '#fff', // fond blanc partout
      py: { xs: 0, md: 0 },
      px: 0,
      position: 'relative',
      overflowX: 'hidden',
    }}>
      {/* Header professionnel centré, sans menu, avec ruban officiel et séparateur décoratif */}
      {/* SUPPRIMÉ: Bloc Header */}
      <Container maxWidth="md" sx={{
        background: 'rgba(255,255,255,0.98)',
        borderRadius: { xs: 0, md: 6 },
        boxShadow: { xs: 'none', md: '0 8px 32px 0 ' + senegalColors.lightBlue },
        px: { xs: 0.5, sm: 2, md: 5 },
        py: { xs: 2, md: 5 },
        minHeight: { xs: 'calc(100vh - 120px)', md: 'auto' },
        width: '100%',
        maxWidth: { xs: '100%', md: 900 },
        mt: { xs: 0, md: 2 },
        mb: 0,
        border: 'none', // plus de contour coloré
        position: 'relative',
        zIndex: 2,
      }}>
        <Typography variant="h4" sx={{ fontWeight: 800, color: senegalColors.darkBlue, mb: 4, textAlign: 'center', letterSpacing: 1, textShadow: '0 2px 8px ' + senegalColors.lightBlue }}>
          Signaler une fraude ou un message suspect
        </Typography>
        <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4, transition: 'all 0.4s cubic-bezier(.4,2,.6,1)' }}>
          {steps.map((label, i) => (
            <Step key={label} sx={{
              '& .MuiStepLabel-label': {
                fontWeight: 700,
                color: senegalColors.blue,
                letterSpacing: 0.5,
                textShadow: '0 1px 4px ' + senegalColors.lightBlue,
              },
              '& .MuiStepIcon-root': {
                color: senegalColors.lightBlue,
                '&.Mui-active': { color: senegalColors.blue },
                '&.Mui-completed': { color: senegalColors.darkBlue },
              },
            }}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>
        {error && <Alert severity="error" sx={{ mb: 2, fontWeight: 600, fontSize: 16, borderRadius: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2, fontWeight: 600, fontSize: 16, borderRadius: 2 }}>{success}</Alert>}
        <Paper elevation={8} sx={{
          p: 4,
          borderRadius: 5,
          background: '#fff', // fond blanc
          boxShadow: '0 8px 32px 0 ' + senegalColors.lightBlue,
          border: 'none', // plus de contour coloré
          transition: 'box-shadow 0.3s',
        }}>
          {renderStepContent()}
        </Paper>
        <Box sx={{ mt: 6, textAlign: 'center', color: '#888', fontSize: 15, opacity: 0.8 }}>
          <span>© {new Date().getFullYear()} République du Sénégal — Plateforme officielle de signalement. Tous droits réservés.</span>
        </Box>
      </Container>
      {/* Footer décoratif */}
      {/* SUPPRIMÉ: Bloc Footer */}
    </Box>
  );
}
