import React, { useState } from "react";
import { Container, Typography, Box, Button, Grid, AppBar, Toolbar, Paper } from "@mui/material";
import { Link, useNavigate } from "react-router-dom";
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

const translations = {
  fr: {
    title: "Plateforme de gestion des anomalies & fraudes",
    subtitle: "Signalez, consultez et g√©rez les fraudes en toute s√©curit√© avec une interface professionnelle et intuitive.",
    login: "Connexion",
    register: "Inscription",
    start: "Signaler",
    brand: "Jub - Jubal - Jubanti"
  },
  en: {
    title: "Anomalies & Fraud Management Platform",
    subtitle: "Report, consult and manage fraud securely with a professional and intuitive interface.",
    login: "Login",
    register: "Register",
    start: "Get Started",
    brand: "Jub - Jubal - Jubanti"
  },
  wo: {
    title: "Platformu jiite ak jubal yi",
    subtitle: "Yeele, wone ak doxal jubal yi ci amna j√†mm ak yoon wi.",
    login: "Jokkoo",
    register: "Inscriwaa",
    start: "Tambali",
    brand: "Jub - Jubal - Jubanti"
  }
};

const signalementTypes = [
  {
    key: 'fraude_bancaire',
    label: 'Fraude bancaire',
    image: '/images/diomaye2.jpg',
    description: 'Transactions suspectes, piratage de compte, etc.'
  },
  {
    key: 'arnaque_en_ligne',
    label: 'Arnaque en ligne',
    image: '/images/diomaye3.jpg',
    description: 'Phishing, fausses boutiques, escroqueries, etc.'
  },
  {
    key: 'corruption',
    label: 'Corruption',
    image: '/images/pngtree-button-senegal-flag-vector-template-design-png-image_859977.png',
    description: 'Corruption, pots-de-vin, abus de pouvoir.'
  },
  {
    key: 'harcelement',
    label: 'Harc√®lement',
    image: '/images/Jubenti.jpg',
    description: 'Harc√®lement moral, sexuel, scolaire, etc.'
  },
  {
    key: 'usurpation',
    label: 'Usurpation d‚Äôidentit√©',
    image: '/images/flag-map-of-senegal-logo-png_seeklogo-312017.png',
    description: 'Faux profils, vol d‚Äôidentit√©, etc.'
  },
  // Ajoutez d‚Äôautres types ici si besoin
];

export default function HomePage() {
  const [lang, setLang] = useState('fr');
  const t = translations[lang];
  const [carouselIndex, setCarouselIndex] = useState(0);
  const navigate = useNavigate();

  const handleLangChange = (newLang) => {
    setLang(newLang);
  };

  const handlePrev = () => {
    setCarouselIndex((prev) => (prev === 0 ? signalementTypes.length - 1 : prev - 1));
  };
  const handleNext = () => {
    setCarouselIndex((prev) => (prev === signalementTypes.length - 1 ? 0 : prev + 1));
  };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(90deg, #f8fafc 0%, #e3eafc 100%)', pb: 8 }}>
      {/* Header */}
      <AppBar position="static" color="transparent" elevation={0} sx={{ py: 1 }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <img src="/images/jub.png" alt="Logo" style={{ width: 60, marginRight: 16 }} />
            <Typography variant="h6" sx={{ fontWeight: 700, color: '#1a237e', letterSpacing: 1 }}>
              {t.brand}
            </Typography>
          </Box>
          <Box sx={{ position: 'relative', display: 'flex', alignItems: 'center' }}>
            <Button component={Link} to="/login" variant="text" sx={{ color: '#1a237e', fontWeight: 600, mx: 1 }}>
              {t.login}
            </Button>
            <Button component={Link} to="/register" variant="contained" sx={{ background: 'linear-gradient(90deg, #1a237e 0%, #1976d2 100%)', color: '#fff', fontWeight: 600, borderRadius: 2, px: 3, mx: 1, boxShadow: 2 }}>
              {t.register}
            </Button>
            <label htmlFor="lang-select" style={{ marginLeft: 16, fontWeight: 600, color: '#1a237e' }}>üåê</label>
            <select
              id="lang-select"
              value={lang}
              onChange={e => handleLangChange(e.target.value)}
              style={{ marginLeft: 8, padding: '6px 12px', borderRadius: 8, border: '1px solid #1a237e', fontWeight: 600, color: '#1a237e', background: '#fff', outline: 'none', cursor: 'pointer' }}
            >
              <option value="fr">Fran√ßais</option>
              <option value="en">English</option>
              <option value="wo">Wolof</option>
            </select>
          </Box>
        </Toolbar>
      </AppBar>
      {/* Hero Section */}
      <Container maxWidth="lg" sx={{ mt: 8 }}>
        <Grid container spacing={4} alignItems="center" justifyContent="center">
          <Grid item xs={12} md={6}>
            <Typography variant="h2" sx={{ fontWeight: 800, color: '#1a237e', mb: 2, lineHeight: 1.1 }}>
              {t.title}
            </Typography>
            <Typography variant="h5" sx={{ color: '#374151', mb: 4 }}>
              {t.subtitle}
            </Typography>
            <Button component={Link} to="/signalement" size="large" variant="contained" sx={{ background: 'linear-gradient(90deg, #1a237e 0%, #1976d2 100%)', color: '#fff', fontWeight: 700, borderRadius: 2, px: 5, py: 1.5, boxShadow: 3 }}>
              {t.start}
            </Button>
          </Grid>
          <Grid item xs={12} md={6} sx={{ display: 'flex', justifyContent: 'center', flexDirection: 'column', alignItems: 'center' }}>
            {/* Carousel de cards signalement */}
            <Box sx={{ width: { xs: 320, md: 400 }, position: 'relative', mb: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <Button onClick={handlePrev} sx={{ minWidth: 0, p: 1, borderRadius: '50%', background: '#e3eafc', mr: 1 }}>
                  <ArrowBackIosNewIcon sx={{ color: '#1976d2' }} />
                </Button>
                <Box sx={{ flex: 1, mx: 1 }}>
                  <Paper elevation={8} sx={{ p: 3, borderRadius: 4, textAlign: 'center', background: '#fff', minHeight: 320, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', boxShadow: '0 4px 24px 0 rgba(26,35,126,0.10)' }}>
                    <img src={signalementTypes[carouselIndex].image} alt={signalementTypes[carouselIndex].label} style={{ width: 120, height: 120, objectFit: 'cover', borderRadius: 16, marginBottom: 16, boxShadow: '0 2px 8px #e3eafc' }} />
                    <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 1 }}>{signalementTypes[carouselIndex].label}</Typography>
                    <Typography variant="body2" sx={{ color: '#374151', mb: 2 }}>{signalementTypes[carouselIndex].description}</Typography>
                    <Button variant="outlined" color="primary" sx={{ borderRadius: 2, fontWeight: 600, px: 3 }} onClick={() => navigate('/signalement')}>
                      Signaler ce type
                    </Button>
                  </Paper>
                </Box>
                <Button onClick={handleNext} sx={{ minWidth: 0, p: 1, borderRadius: '50%', background: '#e3eafc', ml: 1 }}>
                  <ArrowForwardIosIcon sx={{ color: '#1976d2' }} />
                </Button>
              </Box>
              {/* Points de navigation */}
              <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
                {signalementTypes.map((_, idx) => (
                  <Box key={idx} sx={{ width: 12, height: 12, borderRadius: '50%', background: idx === carouselIndex ? '#1976d2' : '#c3cfe2', mx: 0.5, transition: 'background 0.2s' }} />
                ))}
              </Box>
            </Box>
            {/* ...image S√©n√©gal... */}
            <Box sx={{ width: { xs: 240, md: 400 }, height: { xs: 240, md: 400 }, borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
              <img
