export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState("");
  const [form, setForm] = useState({
    motif: "",
    nom: "",
    prenom: "",
    email: "",
    telephone: "",
    description: ""
  });
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  const handleCategory = (cat) => {
    setSelectedCategory(cat);
    setSelectedType("");
    setForm({ ...form, motif: "" });
  };
  const handleType = (type) => {
    setSelectedType(type);
    setForm({ ...form, motif: type });
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleNext = () => {
    if (activeStep === 0 && !selectedCategory) {
      setError("Veuillez s√©lectionner une cat√©gorie de signalement.");
      return;
    }
    if (activeStep === 1 && !selectedType) {
      setError("Veuillez s√©lectionner un type de signalement.");
      return;
    }
    if (activeStep === 2 && (!form.nom || !form.prenom || !form.email)) {
      setError("Veuillez remplir tous les champs personnels obligatoires.");
      return;
    }
    if (activeStep === 3 && !form.description) {
      setError("Merci de d√©crire pr√©cis√©ment le signalement.");
      return;
    }
    setError("");
    setActiveStep((s) => s + 1);
  };

  const handleBack = () => {
    setError("");
    setActiveStep((s) => s - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess("Votre signalement a bien √©t√© transmis. Merci pour votre vigilance.");
    setActiveStep(steps.length - 1);
  };

  // V√©rification de la compl√©tude des formulaires pour chaque type
  const missingTypes = categories.flatMap(cat =>
    cat.types.filter(type => !modularTypeFields[type])
  );

  if (missingTypes.length > 0) {
    console.error("Types de signalement sans formulaire obligatoire :", missingTypes);
  }

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)', py: 6, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Container maxWidth="md">
        <Paper elevation={12} sx={{ borderRadius: 6, p: { xs: 2, sm: 6 }, boxShadow: 10, background: 'rgba(255,255,255,0.98)' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
            <img src="/images/jub.png" alt="Logo S√©n√©gal" style={{ width: 90, marginRight: 32, borderRadius: '50%' }} />
            <Typography variant="h3" sx={{ fontWeight: 800, textAlign: 'center', color: '#1a237e' }}>
              Je signale
            </Typography>
          </Box>
          <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
            <Step><StepLabel>Cat√©gorie</StepLabel></Step>
            <Step><StepLabel>Type</StepLabel></Step>
            <Step><StepLabel>Informations</StepLabel></Step>
            <Step><StepLabel>Confirmation</StepLabel></Step>
          </Stepper>
          {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
          {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
          <form onSubmit={handleSubmit}>
            {/* √âtape 0 : Cat√©gorie */}
            {activeStep === 0 && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>Choisissez une cat√©gorie de signalement :</Typography>
                <Grid container spacing={2}>
                  {categories.map((cat) => (
                    <Grid item xs={12} sm={6} md={4} key={cat.label}>
                      <Paper elevation={selectedCategory === cat ? 8 : 2} sx={{ p: 3, textAlign: 'center', cursor: 'pointer', border: selectedCategory === cat ? '2px solid #1976d2' : '1px solid #c3cfe2', background: selectedCategory === cat ? '#e3eafc' : '#fff', transition: 'all 0.2s' }} onClick={() => { setSelectedCategory(cat); setSelectedType(""); }}>
                        <Typography variant="h3" sx={{ mb: 1 }}>{cat.icon}</Typography>
                        <Typography variant="subtitle1" sx={{ fontWeight: 700 }}>{cat.label}</Typography>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
                <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 3 }}>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => selectedCategory && setActiveStep(1)}
                    sx={{ borderRadius: 2, opacity: selectedCategory ? 1 : 0.6, pointerEvents: selectedCategory ? 'auto' : 'none' }}
                  >
                    Suivant
                  </Button>
                </Box>
              </Box>
            )}
            {/* √âtape 1 : Type */}
            {activeStep === 1 && selectedCategory && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>S√©lectionnez le type de signalement :</Typography>
                <Grid container spacing={2}>
                  {selectedCategory.types.map((type) => (
                    <Grid item xs={12} sm={6} md={6} key={type}>
                      <Paper elevation={selectedType === type ? 8 : 2} sx={{ p: 3, display: 'flex', alignItems: 'center', cursor: 'pointer', border: selectedType === type ? '2px solid #1976d2' : '1px solid #c3cfe2', background: selectedType === type ? '#e3eafc' : '#fff', transition: 'all 0.2s' }} onClick={() => setSelectedType(type)}>
                        <Radio
                          checked={selectedType === type}
                          onChange={() => setSelectedType(type)}
                          color="primary"
                          sx={{ mr: 2 }}
                        />
                        <Typography variant="body1">{type}</Typography>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={() => { setActiveStep(0); setSelectedCategory(null); setSelectedType(""); }} sx={{ borderRadius: 2 }}>Pr√©c√©dent</Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => selectedType && setActiveStep(2)}
                    sx={{ borderRadius: 2 }}
                    disabled={!selectedType}
                  >
                    Suivant
                  </Button>
                </Box>
              </Box>
            )}
            {/* √âtape 2 : Informations (formulaire dynamique selon le type, obligatoire) */}
            {activeStep === 2 && selectedType && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>Informations sur le signalement</Typography>
                {!(modularTypeFields[selectedType]) && (
                  <Alert severity="error" sx={{ mb: 2 }}>
                    Aucun formulaire n'est d√©fini pour ce type de signalement. Veuillez contacter l'administrateur.
                  </Alert>
                )}
                <Grid container spacing={2}>
                  {(modularTypeFields[selectedType] || []).map((field) => {
                    if (field.showIf && !form[field.showIf]) return null;
                    return (
                      <Grid item xs={12} sm={field.type === "textarea" ? 12 : 6} key={field.name}>
                        {field.type === "textarea" ? (
                          <TextField
                            label={field.label}
                            name={field.name}
                            value={form[field.name] || ""}
                            onChange={handleChange}
                            fullWidth
                            required={field.required}
                            multiline
                            rows={4}
                          />
                        ) : field.type === "file" ? (
                          <Button
                            variant="outlined"
                            component="label"
                            fullWidth
                          >
                            {field.label}
                            <input
                              type="file"
                              name={field.name}
                              hidden
                              onChange={e => setForm({ ...form, [field.name]: e.target.files[0] })}
                            />
                          </Button>
                        ) : (
                          <TextField
                            label={field.label}
                            name={field.name}
                            value={form[field.name] || ""}
                            onChange={handleChange}
                            fullWidth
                            required={field.required}
                            type={field.type || "text"}
                          />
                        )}
                      </Grid>
                    );
                  })}
                </Grid>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={() => setActiveStep(1)} sx={{ borderRadius: 2 }}>Pr√©c√©dent</Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => {
                      if (!modularTypeFields[selectedType]) {
                        console.error('Type de signalement sans formulaire :', selectedType);
                        return;
                      }
                      setActiveStep(3);
                    }}
                    sx={{ borderRadius: 2 }}
                    disabled={!modularTypeFields[selectedType]}
                  >
                    Suivant
                  </Button>
                </Box>
              </Box>
            )}
            {/* √âtape 3 : Confirmation */}
            {activeStep === 3 && selectedType && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>R√©capitulatif et confirmation</Typography>
                <Paper variant="outlined" sx={{ p: 3, mb: 3, background: '#f9f9f9' }}>
                  <Grid container spacing={2}>
                    <Grid item xs={12}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 700, mb: 1 }}>Cat√©gorie</Typography>
                      <Typography variant="body2">{selectedCategory?.label}</Typography>
                    </Grid>
                    <Grid item xs={12}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 700, mt: 2, mb: 1 }}>Type</Typography>
                      <Typography variant="body2">{selectedType}</Typography>
                    </Grid>
                    {(modularTypeFields[selectedType] || []).map((field) => {
                      if (field.showIf && !form[field.showIf]) return null;
                      let value = form[field.name];
                      if (field.type === "checkbox") value = value ? "Oui" : "Non";
                      if (field.type === "file" && value) value = value.name;
                      if (!value) value = <span style={{ color: '#aaa' }}>Non renseign√©</span>;
                      return (
                        <React.Fragment key={field.name}>
                          <Grid item xs={6} sm={4}>
                            <Typography variant="subtitle2">{field.label}</Typography>
                          </Grid>
                          <Grid item xs={6} sm={8}>
                            <Typography variant="body2">{value}</Typography>
                          </Grid>
                        </React.Fragment>
                      );
                    })}
                  </Grid>
                </Paper>
                <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 2 }}>
                  <Button variant="outlined" onClick={() => window.print()} sx={{ borderRadius: 2 }}>Imprimer</Button>
                  <Button variant="outlined" onClick={() => {
                    const data = {
                      Cat√©gorie: selectedCategory?.label,
                      Type: selectedType,
                      ...((modularTypeFields[selectedType] || []).reduce((acc, field) => {
                        if (field.showIf && !form[field.showIf]) return acc;
                        acc[field.label] = form[field.name] || '';
                        return acc;
                      }, {}))
                    };
                    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
                    const url = URL.createObjectURL(blob);
                    const a = document.createElement('a');
                    a.href = url;
                    a.download = 'signalement.json';
                    a.click();
                    URL.revokeObjectURL(url);
                  }} sx={{ borderRadius: 2 }}>T√©l√©charger</Button>
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Button variant="outlined" onClick={() => setActiveStep(2)} sx={{ borderRadius: 2 }}>Pr√©c√©dent</Button>
                  <Button variant="contained" color="primary" type="submit" sx={{ borderRadius: 2 }}>Envoyer</Button>
                </Box>
              </Box>
            )}
            {/* √âtape 4 : Merci */}
            {activeStep === 4 && (
              <Box sx={{ textAlign: 'center', py: 6 }}>
                <Typography variant="h4" color="success.main" sx={{ mb: 2 }}>Merci pour votre signalement !</Typography>
                <Typography variant="body1">Votre demande a bien √©t√© prise en compte. Nos √©quipes vont l'√©tudier dans les plus brefs d√©lais.</Typography>
              </Box>
            )}
          </form>
        </Paper>
      </Container>
    </Box>
  );
}
