import React, { useState } from 'react';
import { Box, Container, Paper, Typography, Grid, Button, Alert, TextField, Stepper, Step, StepLabel, MobileStepper, Card, CardContent, CardMedia } from '@mui/material';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';

// Mapping dynamique des types de signalement
const typesSignalement = [
  {
    type: 'sms',
    label: 'SMS suspect',
    description: 'Signaler un SMS indésirable, frauduleux ou suspect.',
    image: '/images/diomaye2.jpg'
  },
  {
    type: 'appel',
    label: 'Appel suspect',
    description: 'Signaler un appel téléphonique indésirable ou frauduleux.',
    image: '/images/diomaye3.jpg'
  },
  {
    type: 'email',
    label: 'Email suspect',
    description: 'Signaler un email de phishing ou frauduleux.',
    image: '/images/pngtree-button-senegal-flag-vector-template-design-png-image_859977.png'
  },
  {
    type: 'autre',
    label: 'Autre type',
    description: 'Signaler un autre type de fraude ou tentative.',
    image: '/images/flag-map-of-senegal-logo-png_seeklogo-312017.png'
  }
];

const steps = [
  'Type de signalement',
  'Informations',
  'Fichiers',
  'Récapitulatif'
];

export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [form, setForm] = useState({});
  const [files, setFiles] = useState({});
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [typeIdx, setTypeIdx] = useState(0);

  // Gestion du carousel
  const handleNextType = () => setTypeIdx((prev) => (prev + 1) % typesSignalement.length);
  const handleBackType = () => setTypeIdx((prev) => (prev - 1 + typesSignalement.length) % typesSignalement.length);

  // Gestion du formulaire
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  const handleFileChange = (e, name) => {
    setFiles({ ...files, [name]: e.target.files });
  };

  // Validation par étape
  const validateStep = () => {
    if (activeStep === 0 && !form.type) {
      setError('Veuillez sélectionner un type de signalement.');
      return false;
    }
    if (activeStep === 1) {
      const fields = [
        { name: 'nom', label: 'Votre nom', required: true },
        { name: 'email', label: 'Votre email', required: true },
        { name: 'telephone', label: 'Votre téléphone', required: true },
        { name: 'description', label: 'Description', required: true }
      ];
      for (const field of fields) {
        if (field.required && !form[field.name]) {
          setError(`Veuillez renseigner le champ : ${field.label}`);
          return false;
        }
      }
    }
    setError("");
    return true;
  };

  // Navigation étapes
  const handleNext = () => {
    if (!validateStep()) return;
    setActiveStep((prev) => prev + 1);
  };
  const handleBack = () => {
    setError("");
    setActiveStep((prev) => prev - 1);
  };

  // Soumission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateStep()) return;
    setSuccess("Signalement transmis ! Merci pour votre vigilance.");
  };

  // Affichage dynamique par étape
  const renderStepContent = () => {
    switch (activeStep) {
      case 0:
        return (
          <Box>
            <Typography variant="h6" sx={{ mb: 2, textAlign: 'center' }}>Quel est le type de signalement&nbsp;?</Typography>
            <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
              <MobileStepper
                variant="dots"
                steps={typesSignalement.length}
                position="static"
                activeStep={typeIdx}
                nextButton={
                  <Button size="small" onClick={handleNextType}>
                    <KeyboardArrowRight />
                  </Button>
                }
                backButton={
                  <Button size="small" onClick={handleBackType}>
                    <KeyboardArrowLeft />
                  </Button>
                }
                sx={{ background: 'transparent' }}
              />
            </Box>
            <Box sx={{ display: 'flex', justifyContent: 'center' }}>
              <Card sx={{ maxWidth: 340, borderRadius: 4, boxShadow: 3, p: 2, cursor: 'pointer', border: form.type === typesSignalement[typeIdx].type ? '2px solid #1976d2' : '2px solid transparent' }}
                onClick={() => setForm({ ...form, type: typesSignalement[typeIdx].type })}>
                <CardMedia
                  component="img"
                  height="120"
                  image={typesSignalement[typeIdx].image}
                  alt={typesSignalement[typeIdx].label}
                  sx={{ objectFit: 'cover', borderRadius: 2 }}
                />
                <CardContent>
                  <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 1, textAlign: 'center' }}>{typesSignalement[typeIdx].label}</Typography>
                  <Typography variant="body2" sx={{ color: '#374151', textAlign: 'center' }}>{typesSignalement[typeIdx].description}</Typography>
                </CardContent>
              </Card>
            </Box>
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
              <Button variant="contained" color="primary" onClick={handleNext} sx={{ borderRadius: 2, fontWeight: 600, px: 4 }}>
                Suivant
              </Button>
            </Box>
          </Box>
        );
      case 1:
        return (
          <Box component="form" onSubmit={handleNext}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Votre nom"
                  name="nom"
                  value={form.nom || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Votre email"
                  name="email"
                  value={form.email || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                  type="email"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Votre téléphone"
                  name="telephone"
                  value={form.telephone || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                  type="tel"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Description"
                  name="description"
                  value={form.description || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                  multiline
                  rows={4}
                />
              </Grid>
            </Grid>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
              <Button onClick={handleBack} sx={{ borderRadius: 2 }}>Retour</Button>
              <Button variant="contained" color="primary" type="submit" sx={{ borderRadius: 2, fontWeight: 600, px: 4 }}>
                Suivant
              </Button>
            </Box>
          </Box>
        );
      case 2:
        return (
          <Box>
            <Typography variant="h6" sx={{ mb: 2 }}>Ajouter des fichiers (facultatif)</Typography>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <Button variant="outlined" component="label" sx={{ borderRadius: 2 }}>
                  Joindre un fichier
                  <input hidden multiple type="file" onChange={(e) => handleFileChange(e, 'piecesJointes')} />
                </Button>
                {files.piecesJointes && files.piecesJointes.length > 0 && (
                  <Typography variant="body2" sx={{ mt: 1 }}>
                    {Array.from(files.piecesJointes).map(f => f.name).join(', ')}
                  </Typography>
                )}
              </Grid>
            </Grid>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
              <Button onClick={handleBack} sx={{ borderRadius: 2 }}>Retour</Button>
              <Button variant="contained" color="primary" onClick={handleNext} sx={{ borderRadius: 2, fontWeight: 600, px: 4 }}>
                Suivant
              </Button>
            </Box>
          </Box>
        );
      case 3:
        return (
          <Box>
            <Typography variant="h6" sx={{ mb: 2 }}>Récapitulatif</Typography>
            <Paper elevation={2} sx={{ p: 3, borderRadius: 3, mb: 2 }}>
              <Typography variant="subtitle1"><b>Type :</b> {typesSignalement.find(t => t.type === form.type)?.label}</Typography>
              <Typography variant="subtitle1"><b>Nom :</b> {form.nom}</Typography>
              <Typography variant="subtitle1"><b>Email :</b> {form.email}</Typography>
              <Typography variant="subtitle1"><b>Téléphone :</b> {form.telephone}</Typography>
              <Typography variant="subtitle1"><b>Description :</b> {form.description}</Typography>
              {files.piecesJointes && files.piecesJointes.length > 0 && (
                <Typography variant="subtitle1"><b>Fichiers joints :</b> {Array.from(files.piecesJointes).map(f => f.name).join(', ')}</Typography>
              )}
            </Paper>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
              <Button onClick={handleBack} sx={{ borderRadius: 2 }}>Retour</Button>
              <Button variant="contained" color="primary" onClick={handleSubmit} sx={{ borderRadius: 2, fontWeight: 600, px: 4 }}>
                Transmettre le signalement
              </Button>
            </Box>
          </Box>
        );
      default:
        return null;
    }
  };

  return (
    <Box sx={{ minHeight: '100vh', background: '#f8fafc', py: 6 }}>
      <Container maxWidth="md">
        <Typography variant="h4" sx={{ fontWeight: 800, color: '#1a237e', mb: 4, textAlign: 'center' }}>
          Signaler une fraude ou un message suspect
        </Typography>
        <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
        <Paper elevation={6} sx={{ p: 4, borderRadius: 4, background: '#fff', boxShadow: '0 4px 24px 0 rgba(26,35,126,0.10)' }}>
          {renderStepContent()}
        </Paper>
      </Container>
    </Box>
  );
}
