import React, { useState } from 'react';
import { Box, Container, Paper, Typography, Stepper, Step, StepLabel, Grid, Button, Alert, Radio, RadioGroup, FormControlLabel, TextField } from '@mui/material';

// Définition des catégories et types
const categories = [
  {
    key: 'espace_public',
    label: 'Espace public et sécurité',
    icon: 'security',
    types: [
      { key: 'incivilite', label: 'Incivilité' },
      { key: 'agression', label: 'Agression' },
      { key: 'vol', label: 'Vol' },
      { key: 'autre_securite', label: 'Autre' }
    ]
  },
  {
    key: 'harcelement',
    label: 'Harcèlement',
    icon: 'report_problem',
    types: [
      { key: 'harcelement_scolaire', label: 'Harcèlement scolaire' },
      { key: 'harcelement_travail', label: 'Harcèlement au travail' },
      { key: 'cyberharcelement', label: 'Cyberharcèlement' },
      { key: 'autre_harcelement', label: 'Autre' }
    ]
  },
  {
    key: 'reseaux_sociaux',
    label: 'Réseaux sociaux',
    icon: 'public',
    types: [
      { key: 'usurpation_compte', label: 'Usurpation de compte' },
      { key: 'fake_news', label: 'Fake news' },
      { key: 'incitation_haine', label: 'Incitation à la haine' },
      { key: 'autre_reseaux', label: 'Autre' }
    ]
  },
  // ...autres catégories existantes si besoin...
];

// Mapping des formulaires dynamiques par type
const modularTypeFields = {
  // Espace public (existant)
  incivilite: [
    { name: 'lieu', label: 'Lieu de l’incivilité', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  agression: [
    { name: 'victime', label: 'Victime concernée', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  vol: [
    { name: 'objet', label: 'Objet volé', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  autre_securite: [
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  // Harcèlement
  harcelement_scolaire: [
    { name: 'etablissement', label: 'Établissement scolaire', type: 'text', required: true },
    { name: 'victime', label: 'Nom de la victime', type: 'text', required: false },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  harcelement_travail: [
    { name: 'entreprise', label: 'Entreprise', type: 'text', required: true },
    { name: 'victime', label: 'Nom de la victime', type: 'text', required: false },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  cyberharcelement: [
    { name: 'plateforme', label: 'Plateforme concernée', type: 'text', required: true },
    { name: 'victime', label: 'Nom de la victime', type: 'text', required: false },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  autre_harcelement: [
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  // Réseaux sociaux
  usurpation_compte: [
    { name: 'reseau', label: 'Réseau social', type: 'text', required: true },
    { name: 'compte', label: 'Nom du compte', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  fake_news: [
    { name: 'reseau', label: 'Réseau social', type: 'text', required: true },
    { name: 'lien', label: 'Lien de la publication', type: 'text', required: false },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  incitation_haine: [
    { name: 'reseau', label: 'Réseau social', type: 'text', required: true },
    { name: 'lien', label: 'Lien de la publication', type: 'text', required: false },
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ],
  autre_reseaux: [
    { name: 'description', label: 'Description', type: 'textarea', required: true }
  ]
  // ...autres types existants si besoin...
};

// Ajout d'une animation décorative globale (à placer tout en haut du fichier ou dans un useEffect global)
const style = document.createElement('style');
style.innerHTML = `
@keyframes pulseCat {
  0% { opacity: 0.5; transform: scale(1); }
  50% { opacity: 0.9; transform: scale(1.15); }
  100% { opacity: 0.5; transform: scale(1); }
}`;
document.head.appendChild(style);

// Page de test simple pour vérifier l'affichage
function SignalementPage() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState(null);
  const [espacePublicMode, setEspacePublicMode] = useState('formulaire'); // 'formulaire' | 'capture' | 'live'
  const [captureFiles, setCaptureFiles] = useState([]); // fichiers pour le mode capture
  const [liveStream, setLiveStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [liveRecordedBlob, setLiveRecordedBlob] = useState(null);
  const [isLive, setIsLive] = useState(false);
  const [liveError, setLiveError] = useState("");

  return (
    <div style={{ padding: 40 }}>
      <h1>Page de signalement</h1>
      <p>Cette page s'affiche correctement.</p>

      {/* Sélecteur de catégorie (exemple simplifié) */}
      <div style={{ marginBottom: 24 }}>
        <label style={{ fontWeight: 600 }}>Choisissez une catégorie :</label>
        <select
          value={selectedCategory?.key || ''}
          onChange={e => {
            const category = categories.find(cat => cat.key === e.target.value);
            setSelectedCategory(category);
            setSelectedType(null);
          }}
          style={{ padding: 10, borderRadius: 8, border: '1px solid #ccc', width: '100%', maxWidth: 400 }}
        >
          <option value="">-- Sélectionnez une catégorie --</option>
          {categories.map(cat => (
            <option key={cat.key} value={cat.key}>{cat.label}</option>
          ))}
        </select>
      </div>

      {/* Sélecteur de type (dynamique selon la catégorie) */}
      {selectedCategory && (
        <div style={{ marginBottom: 24 }}>
          <label style={{ fontWeight: 600 }}>Choisissez un type :</label>
          <select
            value={selectedType?.key || ''}
            onChange={e => {
              const type = selectedCategory.types.find(t => t.key === e.target.value);
              setSelectedType(type);
            }}
            style={{ padding: 10, borderRadius: 8, border: '1px solid #ccc', width: '100%', maxWidth: 400 }}
          >
            <option value="">-- Sélectionnez un type --</option>
            {selectedCategory.types.map(type => (
              <option key={type.key} value={type.key}>{type.label}</option>
            ))}
          </select>
        </div>
      )}

      {/* Bloc de sélection du mode pour la catégorie Espace public et sécurité */}
      {selectedCategory?.key === 'espace_public' && (
        <div style={{ display: 'flex', gap: 16, marginBottom: 24 }}>
          <button
            onClick={() => setEspacePublicMode('formulaire')}
            style={{ padding: 10, borderRadius: 8, border: espacePublicMode === 'formulaire' ? '2px solid #1976d2' : '1px solid #ccc', background: espacePublicMode === 'formulaire' ? '#e3eafc' : '#f8fafc', fontWeight: 600, cursor: 'pointer' }}
          >Formulaire classique</button>
          <button
            onClick={() => setEspacePublicMode('capture')}
            style={{ padding: 10, borderRadius: 8, border: espacePublicMode === 'capture' ? '2px solid #1976d2' : '1px solid #ccc', background: espacePublicMode === 'capture' ? '#e3eafc' : '#f8fafc', fontWeight: 600, cursor: 'pointer' }}
          >Capture (photo/vidéo/doc)</button>
          <button
            onClick={() => setEspacePublicMode('live')}
            style={{ padding: 10, borderRadius: 8, border: espacePublicMode === 'live' ? '2px solid #1976d2' : '1px solid #ccc', background: espacePublicMode === 'live' ? '#e3eafc' : '#f8fafc', fontWeight: 600, cursor: 'pointer' }}
          >Live direct</button>
        </div>
      )}

      {/* Bloc formulaire classique (affiché si mode formulaire ou autre catégorie) */}
      {(selectedCategory?.key !== 'espace_public' || espacePublicMode === 'formulaire') && (
        <div>
          {/* ...formulaire dynamique classique selon modularTypeFields[selectedType?.key]... */}
        </div>
      )}

      {/* Bloc capture (photo/vidéo/documents) */}
      {selectedCategory?.key === 'espace_public' && espacePublicMode === 'capture' && (
        <div style={{ marginBottom: 24 }}>
          <label style={{ fontWeight: 600 }}>Ajoutez vos fichiers (photo, vidéo, PDF, document) :</label>
          <input
            type="file"
            accept="image/*,video/*,.pdf,.doc,.docx,.xls,.xlsx"
            multiple
            onChange={e => {
              const files = Array.from(e.target.files);
              setCaptureFiles(prev => [...prev, ...files]);
            }}
            style={{ display: 'block', margin: '12px 0' }}
          />
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: 12 }}>
            {captureFiles.map((file, idx) => (
              <div key={idx} style={{ border: '1px solid #ccc', borderRadius: 8, padding: 8, textAlign: 'center' }}>
                <div style={{ marginBottom: 6 }}>{file.name}</div>
                {file.type.startsWith('image/') && <img src={URL.createObjectURL(file)} alt="preview" style={{ maxWidth: 80, maxHeight: 60, borderRadius: 4 }} />}
                {file.type.startsWith('video/') && <video src={URL.createObjectURL(file)} controls style={{ maxWidth: 80, maxHeight: 60, borderRadius: 4 }} />}
                {file.type === 'application/pdf' && <a href={URL.createObjectURL(file)} target="_blank" rel="noopener noreferrer">Voir PDF</a>}
                <button onClick={() => setCaptureFiles(captureFiles.filter((_, i) => i !== idx))} style={{ marginTop: 6, color: '#d32f2f', background: 'none', border: 'none', cursor: 'pointer' }}>Supprimer</button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Bloc live direct (enregistrement vidéo) */}
      {selectedCategory?.key === 'espace_public' && espacePublicMode === 'live' && (
        <div style={{ marginBottom: 24, textAlign: 'center' }}>
          {!isLive && !liveRecordedBlob && (
            <button
              onClick={async () => {
                setLiveError("");
                try {
                  const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
                  const recorder = new window.MediaRecorder(stream);
                  setLiveStream(stream);
                  setMediaRecorder(recorder);
                  setRecordedChunks([]);
                  recorder.ondataavailable = (e) => {
                    if (e.data.size > 0) setRecordedChunks(prev => [...prev, e.data]);
                  };
                  recorder.onstop = () => {
                    const blob = new Blob(recordedChunks.concat(), { type: 'video/webm' });
                    setLiveRecordedBlob(blob);
                    stream.getTracks().forEach(track => track.stop());
                    setIsLive(false);
                  };
                  recorder.start();
                  setIsLive(true);
                } catch (err) {
                  setLiveError("Impossible d'accéder à la caméra/microphone.");
                }
              }}
              style={{ padding: 12, borderRadius: 8, background: '#1976d2', color: '#fff', fontWeight: 700, border: 'none', cursor: 'pointer', marginBottom: 12 }}
            >Démarrer l'enregistrement</button>
          )}
          {isLive && (
            <>
              <video
                autoPlay
                muted
                ref={el => { if (el && liveStream) el.srcObject = liveStream; }}
                style={{ width: 320, height: 240, borderRadius: 8, background: '#000', marginBottom: 8 }}
              />
              <button
                onClick={() => { if (mediaRecorder && mediaRecorder.state === 'recording') mediaRecorder.stop(); }}
                style={{ padding: 10, borderRadius: 8, background: '#d32f2f', color: '#fff', fontWeight: 700, border: 'none', cursor: 'pointer', marginRight: 8 }}
              >Arrêter et enregistrer</button>
              <button
                onClick={() => {
                  if (liveStream) liveStream.getTracks().forEach(track => track.stop());
                  setIsLive(false);
                  setMediaRecorder(null);
                  setLiveStream(null);
                  setRecordedChunks([]);
                }}
                style={{ padding: 10, borderRadius: 8, background: '#e3eafc', color: '#1976d2', fontWeight: 700, border: 'none', cursor: 'pointer' }}
              >Annuler</button>
            </>
          )}
          {liveRecordedBlob && !isLive && (
            <div style={{ textAlign: 'center', marginBottom: 12 }}>
              <video src={URL.createObjectURL(liveRecordedBlob)} controls style={{ width: 320, height: 240, borderRadius: 8, background: '#000', marginBottom: 8 }} />
              <div>
                <a href={URL.createObjectURL(liveRecordedBlob)} download="video_signalement.webm" style={{ marginRight: 12, color: '#1976d2', fontWeight: 600 }}>Télécharger la vidéo</a>
                <button onClick={() => { setLiveRecordedBlob(null); setRecordedChunks([]); }} style={{ color: '#d32f2f', background: 'none', border: 'none', cursor: 'pointer' }}>Supprimer</button>
              </div>
            </div>
          )}
          {liveError && <div style={{ color: '#d32f2f', marginTop: 8 }}>{liveError}</div>}
        </div>
      )}

      {/* Bloc récapitulatif confirmation (à l'étape finale) */}
      {selectedCategory?.key === 'espace_public' && (
        <div style={{ marginTop: 32, padding: 16, borderRadius: 8, border: '1px solid #ccc', background: '#f9f9f9' }}>
          <h2 style={{ margin: 0, fontSize: 18 }}>Récapitulatif de votre signalement</h2>
          <div style={{ marginTop: 12 }}>
            {espacePublicMode === 'formulaire' && (
              <div>
                <p style={{ margin: 0 }}>Merci d'avoir rempli le formulaire. Votre signalement est pris en compte.</p>
              </div>
            )}
            {espacePublicMode === 'capture' && captureFiles.length > 0 && (
              <div>
                <p style={{ margin: 0 }}>Fichiers ajoutés :</p>
                <ul style={{ paddingLeft: 20, marginTop: 8 }}>
                  {captureFiles.map((file, idx) => (
                    <li key={idx} style={{ marginBottom: 4 }}>{file.name}</li>
                  ))}
                </ul>
                <p style={{ margin: 0, marginTop: 8 }}>Merci d'avoir fourni ces fichiers. Votre signalement est pris en compte.</p>
              </div>
            )}
            {espacePublicMode === 'live' && liveRecordedBlob && (
              <div>
                <p style={{ margin: 0 }}>Vidéo enregistrée :</p>
                <video src={URL.createObjectURL(liveRecordedBlob)} controls style={{ width: '100%', maxWidth: 400, borderRadius: 4, marginTop: 8 }} />
                <p style={{ margin: 0, marginTop: 8 }}>Merci d'avoir enregistré une vidéo. Votre signalement est pris en compte.</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default SignalementPage;
