import React, { useState } from 'react';
import { Box, Container, Paper, Typography, Stepper, Step, StepLabel, Grid, Button, Alert, TextField } from '@mui/material';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

// Types de signalement pour le carousel
const signalementTypes = [
  {
    key: 'fraude_bancaire',
    label: 'Fraude bancaire',
    image: '/images/diomaye2.jpg',
    description: 'Transactions suspectes, piratage de compte, etc.'
  },
  {
    key: 'arnaque_en_ligne',
    label: 'Arnaque en ligne',
    image: '/images/diomaye3.jpg',
    description: 'Phishing, fausses boutiques, escroqueries, etc.'
  },
  {
    key: 'corruption',
    label: 'Corruption',
    image: '/images/pngtree-button-senegal-flag-vector-template-design-png-image_859977.png',
    description: 'Corruption, pots-de-vin, abus de pouvoir.'
  },
  {
    key: 'harcelement',
    label: 'Harcèlement',
    image: '/images/Jubenti.jpg',
    description: 'Harcèlement moral, sexuel, scolaire, etc.'
  },
  {
    key: 'usurpation',
    label: 'Usurpation d’identité',
    image: '/images/flag-map-of-senegal-logo-png_seeklogo-312017.png',
    description: 'Faux profils, vol d’identité, etc.'
  },
];

// Mapping des champs par type (exemple simplifié)
const typeFields = {
  fraude_bancaire: [
    { name: 'banque', label: 'Banque concernée', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true },
    { name: 'preuve', label: 'Pièce jointe', type: 'file', required: false }
  ],
  arnaque_en_ligne: [
    { name: 'site', label: 'Site ou plateforme', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true },
    { name: 'preuve', label: 'Pièce jointe', type: 'file', required: false }
  ],
  corruption: [
    { name: 'organisme', label: 'Organisme ou personne', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true },
    { name: 'preuve', label: 'Pièce jointe', type: 'file', required: false }
  ],
  harcelement: [
    { name: 'contexte', label: 'Contexte', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true },
    { name: 'preuve', label: 'Pièce jointe', type: 'file', required: false }
  ],
  usurpation: [
    { name: 'plateforme', label: 'Plateforme', type: 'text', required: true },
    { name: 'description', label: 'Description', type: 'textarea', required: true },
    { name: 'preuve', label: 'Pièce jointe', type: 'file', required: false }
  ]
};

const steps = [
  'Type de signalement',
  'Informations',
  'Pièces jointes',
  'Récapitulatif'
];

export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [carouselIndex, setCarouselIndex] = useState(0);
  const [selectedType, setSelectedType] = useState(null);
  const [form, setForm] = useState({});
  const [files, setFiles] = useState({});
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Carousel navigation
  const handlePrev = () => setCarouselIndex((prev) => (prev === 0 ? signalementTypes.length - 1 : prev - 1));
  const handleNext = () => setCarouselIndex((prev) => (prev === signalementTypes.length - 1 ? 0 : prev + 1));

  // Sélection du type via le carousel
  const handleSelectType = () => {
    setSelectedType(signalementTypes[carouselIndex].key);
    setActiveStep(1);
  };

  // Gestion du formulaire
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  const handleFileChange = (e, name) => {
    setFiles({ ...files, [name]: e.target.files });
  };

  // Navigation étapes
  const handleNextStep = () => setActiveStep((s) => s + 1);
  const handleBackStep = () => setActiveStep((s) => s - 1);

  // Validation simple
  const validateStep = () => {
    if (activeStep === 1 && selectedType) {
      const fields = typeFields[selectedType] || [];
      for (const field of fields) {
        if (field.required && !form[field.name]) {
          setError(`Veuillez renseigner le champ : ${field.label}`);
          return false;
        }
      }
    }
    setError("");
    return true;
  };

  // Soumission
  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess("Signalement transmis ! Merci pour votre vigilance.");
    setActiveStep(steps.length - 1);
  };

  return (
    <Box sx={{ minHeight: '100vh', background: '#f8fafc', py: 6 }}>
      <Container maxWidth="md">
        {/* Stepper horizontal */}
        <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}><StepLabel>{label}</StepLabel></Step>
          ))}
        </Stepper>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
        {/* Étape 0 : Carousel de types de signalement */}
        {activeStep === 0 && (
          <Box sx={{ width: { xs: 320, md: 500 }, mx: 'auto', mb: 5 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
              <Button onClick={handlePrev} sx={{ minWidth: 0, p: 1, borderRadius: '50%', background: '#e3eafc', mr: 1 }}>
                <ArrowBackIosNewIcon sx={{ color: '#1976d2' }} />
              </Button>
              <Box sx={{ flex: 1, mx: 1 }}>
                <Paper elevation={8} sx={{ p: 3, borderRadius: 4, textAlign: 'center', background: '#fff', minHeight: 320, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', boxShadow: '0 4px 24px 0 rgba(26,35,126,0.10)' }}>
                  <img src={signalementTypes[carouselIndex].image} alt={signalementTypes[carouselIndex].label} style={{ width: 120, height: 120, objectFit: 'cover', borderRadius: 16, marginBottom: 16, boxShadow: '0 2px 8px #e3eafc' }} />
                  <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 1 }}>{signalementTypes[carouselIndex].label}</Typography>
                  <Typography variant="body2" sx={{ color: '#374151', mb: 2 }}>{signalementTypes[carouselIndex].description}</Typography>
                  <Button variant="contained" color="primary" sx={{ borderRadius: 2, fontWeight: 600, px: 3 }} onClick={handleSelectType}>
                    Choisir ce type
                  </Button>
                </Paper>
              </Box>
              <Button onClick={handleNext} sx={{ minWidth: 0, p: 1, borderRadius: '50%', background: '#e3eafc', ml: 1 }}>
                <ArrowForwardIosIcon sx={{ color: '#1976d2' }} />
              </Button>
            </Box>
            {/* Points de navigation */}
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
              {signalementTypes.map((_, idx) => (
                <Box key={idx} sx={{ width: 12, height: 12, borderRadius: '50%', background: idx === carouselIndex ? '#1976d2' : '#c3cfe2', mx: 0.5, transition: 'background 0.2s' }} />
              ))}
            </Box>
          </Box>
        )}
        {/* Étape 1 : Formulaire dynamique selon le type */}
        {activeStep === 1 && selectedType && (
          <form onSubmit={e => { e.preventDefault(); if (validateStep()) handleNextStep(); }}>
            <Typography variant="h6" sx={{ mb: 2 }}>Informations sur le signalement</Typography>
            <Grid container spacing={2}>
              {(typeFields[selectedType] || []).map((field) => (
                <Grid item xs={12} key={field.name}>
                  {field.type === 'textarea' ? (
                    <TextField
                      label={field.label}
                      name={field.name}
                      value={form[field.name] || ''}
                      onChange={handleChange}
                      fullWidth
                      required={field.required}
                      multiline
                      rows={4}
                    />
                  ) : field.type === 'file' ? (
                    <Button variant="outlined" component="label" fullWidth>
                      {field.label}
                      <input type="file" name={field.name} hidden onChange={e => handleFileChange(e, field.name)} />
                    </Button>
                  ) : (
                    <TextField
                      label={field.label}
                      name={field.name}
                      value={form[field.name] || ''}
                      onChange={handleChange}
                      fullWidth
                      required={field.required}
                      type={field.type || 'text'}
                    />
                  )}
                </Grid>
              ))}
            </Grid>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
              <Button variant="outlined" onClick={handleBackStep} sx={{ borderRadius: 2 }}>Précédent</Button>
              <Button variant="contained" color="primary" type="submit" sx={{ borderRadius: 2 }}>Suivant</Button>
            </Box>
          </form>
        )}
        {/* Étape 2 : Pièces jointes (affichage des fichiers joints) */}
        {activeStep === 2 && selectedType && (
          <Box>
            <Typography variant="h6" sx={{ mb: 2 }}>Pièces jointes</Typography>
            <Grid container spacing={2}>
              {(typeFields[selectedType] || []).filter(f => f.type === 'file').map((field) => {
                const value = files[field.name];
                if (!value) return null;
                const fileArr = Array.from(value);
                return fileArr.map((file, idx) => (
                  <Grid item xs={12} sm={6} md={4} key={field.name + idx}>
                    <Paper sx={{ p: 2, mb: 1, background: '#fff', borderRadius: 2, boxShadow: '0 1px 6px 0 rgba(26,35,126,0.06)', textAlign: 'center' }}>
                      <Typography variant="body2" sx={{ fontWeight: 500, mb: 1 }}>{file.name}</Typography>
                      {file.type && file.type.startsWith('image/') && (
                        <img src={URL.createObjectURL(file)} alt={file.name} style={{ maxWidth: 180, maxHeight: 140, borderRadius: 8, marginBottom: 8, border: '1px solid #e3eafc' }} />
                      )}
                      {file.type && file.type.startsWith('video/') && (
                        <video src={URL.createObjectURL(file)} controls style={{ maxWidth: 180, maxHeight: 140, borderRadius: 8, marginBottom: 8, border: '1px solid #e3eafc' }} />
                      )}
                      {file.type === 'application/pdf' && (
                        <a href={URL.createObjectURL(file)} target="_blank" rel="noopener noreferrer" style={{ color: '#1976d2', textDecoration: 'underline', display: 'block', marginBottom: 8 }}>Voir le PDF</a>
                      )}
                    </Paper>
                  </Grid>
                ));
              })}
            </Grid>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
              <Button variant="outlined" onClick={handleBackStep} sx={{ borderRadius: 2 }}>Précédent</Button>
              <Button variant="contained" color="primary" onClick={handleNextStep} sx={{ borderRadius: 2 }}>Suivant</Button>
            </Box>
          </Box>
        )}
        {/* Étape 3 : Récapitulatif */}
        {activeStep === 3 && selectedType && (
          <Box>
            <Typography variant="h6" sx={{ mb: 2, color: '#1a237e', fontWeight: 700 }}>Récapitulatif et confirmation</Typography>
            <Paper variant="outlined" sx={{ p: 4, mb: 3, background: '#f4f7fb', borderRadius: 4, boxShadow: '0 2px 12px 0 rgba(26,35,126,0.08)' }}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" sx={{ fontWeight: 700, color: '#1976d2', mb: 0.5 }}>Type de signalement</Typography>
                  <Typography variant="body1" sx={{ color: '#222', fontWeight: 500 }}>{signalementTypes.find(t => t.key === selectedType)?.label}</Typography>
                </Grid>
                {(typeFields[selectedType] || []).map((field) => {
                  if (field.type === 'file') return null;
                  const value = form[field.name];
                  if (!value) return null;
                  return (
                    <Grid item xs={12} sm={6} key={field.name}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 700, color: '#1976d2', mb: 0.5 }}>{field.label}</Typography>
                      <Typography variant="body1" sx={{ color: '#222', fontWeight: 500, wordBreak: 'break-word' }}>{value}</Typography>
                    </Grid>
                  );
                })}
                {(typeFields[selectedType] || []).filter(f => f.type === 'file').map((field) => {
                  const value = files[field.name];
                  if (!value) return null;
                  const fileArr = Array.from(value);
                  return fileArr.map((file, idx) => (
                    <Grid item xs={12} sm={6} md={4} key={field.name + idx}>
                      <Paper sx={{ p: 2, mb: 1, background: '#fff', borderRadius: 2, boxShadow: '0 1px 6px 0 rgba(26,35,126,0.06)', textAlign: 'center' }}>
                        <Typography variant="body2" sx={{ fontWeight: 500, mb: 1 }}>{file.name}</Typography>
                        {file.type && file.type.startsWith('image/') && (
                          <img src={URL.createObjectURL(file)} alt={file.name} style={{ maxWidth: 180, maxHeight: 140, borderRadius: 8, marginBottom: 8, border: '1px solid #e3eafc' }} />
                        )}
                        {file.type && file.type.startsWith('video/') && (
                          <video src={URL.createObjectURL(file)} controls style={{ maxWidth: 180, maxHeight: 140, borderRadius: 8, marginBottom: 8, border: '1px solid #e3eafc' }} />
                        )}
                        {file.type === 'application/pdf' && (
                          <a href={URL.createObjectURL(file)} target="_blank" rel="noopener noreferrer" style={{ color: '#1976d2', textDecoration: 'underline', display: 'block', marginBottom: 8 }}>Voir le PDF</a>
                        )}
                      </Paper>
                    </Grid>
                  ));
                })}
              </Grid>
            </Paper>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
              <Button variant="outlined" onClick={handleBackStep} sx={{ borderRadius: 2 }}>Précédent</Button>
              <Button variant="contained" color="primary" onClick={handleSubmit} sx={{ borderRadius: 2, fontWeight: 700, px: 4 }}>
                Confirmer l'envoi
              </Button>
            </Box>
          </Box>
        )}
      </Container>
    </Box>
  );
}
