import React, { useState } from "react";
import { Container, Box, Typography, Paper, Button, Grid, Checkbox, FormControlLabel, TextField, Alert } from "@mui/material";
import { useLocation, useNavigate } from "react-router-dom";

export default function TypeSignalementPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { category } = location.state || {};
  const [checkedTypes, setCheckedTypes] = useState([]);
  const [form, setForm] = useState({
    nom: "",
    prenom: "",
    email: "",
    telephone: "",
    description: ""
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Récupération de la catégorie depuis localStorage si présente
  React.useEffect(() => {
    if (!category) {
      const cat = localStorage.getItem("selectedCategory");
      if (cat) {
        navigate("/type-signalement", { state: { category: JSON.parse(cat) } });
      } else {
        navigate("/signalement");
      }
    }
  }, [category, navigate]);

  if (!category) {
    return null;
  }

  const handleCheck = (type) => {
    setCheckedTypes((prev) =>
      prev.includes(type) ? prev.filter((t) => t !== type) : [...prev, type]
    );
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.nom || !form.prenom || !form.email || checkedTypes.length === 0 || !form.description) {
      setError("Veuillez remplir tous les champs obligatoires et choisir au moins un type de signalement.");
      return;
    }
    setError("");
    setSuccess("Votre signalement a bien été transmis. Merci pour votre vigilance.");
  };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)', py: 6, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Container maxWidth="md">
        <Paper elevation={12} sx={{ borderRadius: 6, p: { xs: 2, sm: 6 }, boxShadow: 10, background: 'rgba(255,255,255,0.98)' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
            <Typography variant="h3" sx={{ fontWeight: 800, color: '#1a237e' }}>
              {category.icon} {category.label}
            </Typography>
          </Box>
          <Typography variant="h6" sx={{ mb: 2 }}>Sélectionnez le(s) type(s) de signalement :</Typography>
          <Grid container spacing={2} sx={{ mb: 3 }}>
            {category.types.map((type) => (
              <Grid item xs={12} sm={6} md={4} key={type}>
                <FormControlLabel
                  control={<Checkbox checked={checkedTypes.includes(type)} onChange={() => handleCheck(type)} />}
                  label={type}
                />
              </Grid>
            ))}
          </Grid>
          <form onSubmit={handleSubmit}>
            <Typography variant="h6" sx={{ mb: 2 }}>Vos informations</Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField label="Nom" name="nom" value={form.nom} onChange={handleChange} fullWidth required />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField label="Prénom" name="prenom" value={form.prenom} onChange={handleChange} fullWidth required />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField label="Email" name="email" value={form.email} onChange={handleChange} fullWidth required type="email" />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField label="Téléphone (optionnel)" name="telephone" value={form.telephone} onChange={handleChange} fullWidth />
              </Grid>
              <Grid item xs={12}>
                <TextField label="Description détaillée" name="description" value={form.description} onChange={handleChange} fullWidth required multiline rows={4} />
              </Grid>
            </Grid>
            {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
            {success && <Alert severity="success" sx={{ mt: 2 }}>{success}</Alert>}
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 4 }}>
              <Button type="submit" variant="contained" size="large" sx={{ background: 'linear-gradient(90deg, #1a237e 0%, #1976d2 100%)', color: '#fff', fontWeight: 700, borderRadius: '50px', px: 5, py: 1.5, boxShadow: 3 }}>
                Envoyer le signalement
              </Button>
            </Box>
          </form>
        </Paper>
      </Container>
    </Box>
  );
}
