// Ajout d'un mapping spÃ©cial pour la catÃ©gorie "Espace public et sÃ©curitÃ©" avec choix du mode de signalement
const espacePublicTypes = [
  "Accident sur voie publique (vÃ©hicule, piÃ©ton, vÃ©lo...)",
  "Danger sur la route (chaussÃ©e, signalisation dÃ©fectueuse)",
  "Agression ou vol dans la rue",
  "DÃ©gradation ou vandalisme urbain",
  "Occupation illÃ©gale de lâ€™espace public",
  "Tapage, nuisances, troubles Ã  lâ€™ordre public"
];

export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState("");
  const [form, setForm] = useState({
    motif: "",
    nom: "",
    prenom: "",
    email: "",
    telephone: "",
    description: ""
  });
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");
  const [espacePublicMode, setEspacePublicMode] = useState('formulaire');
  const [liveStream, setLiveStream] = useState(null);
  const [isLive, setIsLive] = useState(false);
  const [liveError, setLiveError] = useState("");
  const [liveRecordedBlob, setLiveRecordedBlob] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedChunks, setRecordedChunks] = useState([]);

  const handleCategory = (cat) => {
    setSelectedCategory(cat);
    setSelectedType("");
    setForm({ ...form, motif: "" });
  };
  const handleType = (type) => {
    setSelectedType(type);
    setForm({ ...form, motif: type });
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleNext = () => {
    if (activeStep === 0 && !selectedCategory) {
      setError("Veuillez sÃ©lectionner une catÃ©gorie de signalement.");
      return;
    }
    if (activeStep === 1 && !selectedType) {
      setError("Veuillez sÃ©lectionner un type de signalement.");
      return;
    }
    // Validation spÃ©cifique pour la catÃ©gorie Espace public et sÃ©curitÃ©
    if (activeStep === 2 && espacePublicTypes.includes(selectedType)) {
      if (espacePublicMode === 'formulaire') {
        // VÃ©rifier les champs obligatoires du formulaire
        const requiredFields = (modularTypeFields[selectedType] || []).filter(f => f.required);
        for (const field of requiredFields) {
          if (!form[field.name]) {
            setError(`Veuillez renseigner le champ obligatoire : ${field.label}`);
            return;
          }
        }
      } else if (espacePublicMode === 'capture') {
        if (!form.espacePublicCapture || !Array.isArray(form.espacePublicCapture) || form.espacePublicCapture.length === 0) {
          setError('Veuillez joindre au moins un fichier (photo, vidÃ©o ou document).');
          return;
        }
      } else if (espacePublicMode === 'live') {
        if (!liveRecordedBlob) {
          setError('Veuillez enregistrer une vidÃ©o live avant de continuer.');
          return;
        }
      }
    }
    // Validation classique pour les autres catÃ©gories
    if (activeStep === 2 && !espacePublicTypes.includes(selectedType)) {
      if (!form.nom || !form.prenom || !form.email) {
        setError("Veuillez remplir tous les champs personnels obligatoires.");
        return;
      }
    }
    setError("");
    setActiveStep((s) => s + 1);
  };

  const handleBack = () => {
    setError("");
    setActiveStep((s) => s - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess("Votre signalement a bien Ã©tÃ© transmis. Merci pour votre vigilance.");
    setActiveStep(steps.length - 1);
  };

  // VÃ©rification de la complÃ©tude des formulaires pour chaque type
  const missingTypes = categories.flatMap(cat =>
    cat.types.filter(type => !modularTypeFields[type])
  );

  if (missingTypes.length > 0) {
    console.error("Types de signalement sans formulaire obligatoire :", missingTypes);
  }

  // Gestion de la prÃ©visualisation et suppression de fichiers pour la capture (photo/vidÃ©o ou document)
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    // VÃ©rification du type (image, vidÃ©o, pdf, doc, etc.)
    const allowedTypes = [
      'image/jpeg', 'image/png', 'image/gif', 'image/webp', 'image/bmp',
      'video/mp4', 'video/webm', 'video/ogg',
      'application/pdf',
      'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      'application/vnd.ms-excel', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    ];
    if (!allowedTypes.includes(file.type)) {
      setError('Format de fichier non supportÃ©.');
      return;
    }
    setForm({ ...form, espacePublicCapture: file });
    setError("");
  };
  const handleRemoveFile = () => {
    setForm({ ...form, espacePublicCapture: null });
  };

  return (
    <Box sx={{ minHeight: '100vh', background: '#fff', py: 6, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Container maxWidth="md">
