                                    <a href={URL.createObjectURL(file)} target="_blank" rel="noopener noreferrer" style={{ color: '#1976d2', textDecoration: 'underline', display: 'block', marginBottom: 8 }}>Voir le PDF</a>
                                  )}
                                  {['application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'application/vnd.ms-excel', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'].includes(file.type) && (
                                    <a href={URL.createObjectURL(file)} target="_blank" rel="noopener noreferrer" style={{ color: '#1976d2', textDecoration: 'underline', display: 'block', marginBottom: 8 }}>TÃ©lÃ©charger le document</a>
                                  )}
                                </Paper>
                              </Grid>
                            ))}
                          </Grid>
                        </Grid>
                      );
                    })}
                    {/* Bloc donnÃ©es du formulaire dynamique (hors fichiers) */}
                    {(espacePublicMode === 'formulaire' || !espacePublicTypes.includes(selectedType)) && (
                      (modularTypeFields[selectedType] || []).filter(field => field.type !== 'file').map((field) => {
                        const value = form[field.name];
                        if (!value) return null;
                        return (
                          <Grid item xs={12} sm={6} key={field.name}>
                            <Typography variant="subtitle2" sx={{ fontWeight: 700, color: '#1976d2', mb: 0.5 }}>{field.label}</Typography>
                            <Typography variant="body1" sx={{ color: '#222', fontWeight: 500, wordBreak: 'break-word' }}>{Array.isArray(value) ? value.join(', ') : value}</Typography>
                          </Grid>
                        );
                      })
                    )}
                  </Grid>
                </Paper>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={() => setActiveStep(2)} sx={{ borderRadius: 2 }}>PrÃ©cÃ©dent</Button>
                  <Button
                    variant="contained"
                    color="primary"
                    type="submit"
                    sx={{ borderRadius: 2, fontWeight: 700, px: 4 }}
                  >
                    Confirmer l'envoi
                  </Button>
                </Box>
              </Box>
            )}
          </form>
        </Paper>
      </Container>
    </Box>
  );
}
