import React, { useEffect, useState } from "react";
import { Container, Box, Typography, Paper, Alert, Button, Grid, TextField, Dialog, DialogTitle, DialogContent, DialogActions, CircularProgress } from "@mui/material";
import axios from "axios";

export default function ProfilePage() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({ nom: "", prenom: "", email: "" });
  const [loading, setLoading] = useState(false);
  const [deleteDialog, setDeleteDialog] = useState(false);
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("/api/auth/me", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUser(res.data);
        setForm({ nom: res.data.nom || "", prenom: res.data.prenom || "", email: res.data.email || "" });
      } catch (err) {
        setError("Impossible de charger le profil.");
      }
    };
    fetchProfile();
  }, []);

  const handleEdit = () => setEditMode(true);
  const handleCancelEdit = () => { setEditMode(false); setForm({ nom: user.nom || "", prenom: user.prenom || "", email: user.email || "" }); };
  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSave = async () => {
    setLoading(true);
    setError("");
    setSuccess("");
    try {
      const token = localStorage.getItem("token");
      const res = await axios.put("/api/auth/me", form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(res.data);
      setEditMode(false);
      setSuccess("Profil mis à jour avec succès.");
    } catch (err) {
      setError(err.response?.data?.error || "Erreur lors de la mise à jour");
    }
    setLoading(false);
  };

  const handleDelete = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token");
      await axios.delete("/api/auth/me", {
        headers: { Authorization: `Bearer ${token}` }
      });
      localStorage.removeItem("token");
      window.location.href = "/";
    } catch (err) {
      setError("Erreur lors de la suppression du compte");
    }
    setLoading(false);
  };

  if (error) return <Alert severity="error">{error}</Alert>;
  if (!user) return <Box sx={{ display: 'flex', justifyContent: 'center', mt: 8 }}><CircularProgress /></Box>;

  return (
    <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)' }}>
      <Container maxWidth="sm">
        <Paper elevation={8} sx={{ p: 4, borderRadius: 4, boxShadow: 6 }}>
          <Typography variant="h4" align="center" gutterBottom fontWeight={700} color="primary.main">
            Mon Profil
          </Typography>
          {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
          <Grid container spacing={2} sx={{ mt: 2 }}>
            <Grid item xs={12} sm={6}><b>Nom :</b> {user.nom}</Grid>
            <Grid item xs={12} sm={6}><b>Prénom :</b> {user.prenom}</Grid>
            <Grid item xs={12} sm={12}><b>Email :</b> {user.email}</Grid>
            <Grid item xs={12} sm={12}><b>Nom d'utilisateur :</b> {user.username}</Grid>
            <Grid item xs={12} sm={12}><b>Créé le :</b> {user.createdAt && new Date(user.createdAt).toLocaleDateString()}</Grid>
          </Grid>
          <Box sx={{ display: 'flex', gap: 2, mt: 4, justifyContent: 'center' }}>
            <Button variant="contained" color="primary" onClick={handleEdit} sx={{ fontWeight: 600 }}>Modifier</Button>
            <Button variant="outlined" color="error" onClick={() => setDeleteDialog(true)} sx={{ fontWeight: 600 }}>Supprimer le compte</Button>
            <Button variant="text" color="secondary" onClick={() => { localStorage.removeItem("token"); window.location.href = "/login"; }}>Déconnexion</Button>
          </Box>
        </Paper>
        <Dialog open={editMode} onClose={handleCancelEdit} maxWidth="xs" fullWidth>
          <DialogTitle>Modifier mon profil</DialogTitle>
          <DialogContent sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
            <TextField label="Nom" name="nom" value={form.nom} onChange={handleChange} fullWidth autoFocus />
            <TextField label="Prénom" name="prenom" value={form.prenom} onChange={handleChange} fullWidth />
            <TextField label="Email" name="email" value={form.email} onChange={handleChange} fullWidth type="email" />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCancelEdit} color="secondary">Annuler</Button>
            <Button onClick={handleSave} color="primary" variant="contained" disabled={loading}>{loading ? <CircularProgress size={20} /> : "Enregistrer"}</Button>
          </DialogActions>
        </Dialog>
        <Dialog open={deleteDialog} onClose={() => setDeleteDialog(false)}>
          <DialogTitle>Confirmer la suppression</DialogTitle>
          <DialogContent>Voulez-vous vraiment supprimer votre compte ? Cette action est irréversible.</DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteDialog(false)} color="secondary">Annuler</Button>
            <Button onClick={handleDelete} color="error" variant="contained" disabled={loading}>{loading ? <CircularProgress size={20} /> : "Supprimer"}</Button>
          </DialogActions>
        </Dialog>
      </Container>
    </Box>
  );
}
