import React, { useState } from 'react';
import { Box, Container, Paper, Typography, Grid, Button, Alert, TextField, Stepper, Step, StepLabel, MobileStepper, Card, CardContent, CardMedia } from '@mui/material';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';

// Mapping dynamique des catégories et types
const categories = [
  {
    id: 'communication',
    label: 'Communication suspecte',
    description: 'SMS, appels, emails ou messages suspects.',
    image: '/images/diomaye2.jpg',
    types: [
      {
        id: 'sms',
        label: 'SMS suspect',
        description: 'Signaler un SMS indésirable, frauduleux ou suspect.',
        form: [
          { name: 'expediteur', label: 'Expéditeur', type: 'text', required: true },
          { name: 'numero', label: 'Numéro reçu', type: 'text', required: true },
          { name: 'contenu', label: 'Contenu du SMS', type: 'textarea', required: true },
          { name: 'date', label: 'Date de réception', type: 'date', required: true }
        ]
      },
      {
        id: 'appel',
        label: 'Appel suspect',
        description: 'Signaler un appel téléphonique indésirable ou frauduleux.',
        form: [
          { name: 'numero', label: 'Numéro appelant', type: 'text', required: true },
          { name: 'date', label: 'Date de l\'appel', type: 'date', required: true },
          { name: 'heure', label: 'Heure de l\'appel', type: 'time', required: true },
          { name: 'details', label: 'Détails de l\'appel', type: 'textarea', required: true }
        ]
      },
      {
        id: 'email',
        label: 'Email suspect',
        description: 'Signaler un email de phishing ou frauduleux.',
        form: [
          { name: 'expediteur', label: 'Expéditeur', type: 'text', required: true },
          { name: 'objet', label: 'Objet de l\'email', type: 'text', required: true },
          { name: 'contenu', label: 'Contenu de l\'email', type: 'textarea', required: true },
          { name: 'date', label: 'Date de réception', type: 'date', required: true }
        ]
      }
    ]
  },
  {
    id: 'finance',
    label: 'Fraude financière',
    description: 'Tentative de fraude bancaire, carte, virement, etc.',
    image: '/images/diomaye3.jpg',
    types: [
      {
        id: 'virement',
        label: 'Virement frauduleux',
        description: 'Signaler une tentative de virement frauduleux.',
        form: [
          { name: 'banque', label: 'Banque concernée', type: 'text', required: true },
          { name: 'montant', label: 'Montant', type: 'number', required: true },
          { name: 'date', label: 'Date de la tentative', type: 'date', required: true },
          { name: 'details', label: 'Détails', type: 'textarea', required: true }
        ]
      },
      {
        id: 'cb',
        label: 'Carte bancaire',
        description: 'Signaler une fraude à la carte bancaire.',
        form: [
          { name: 'banque', label: 'Banque', type: 'text', required: true },
          { name: 'date', label: 'Date de l\'incident', type: 'date', required: true },
          { name: 'details', label: 'Détails', type: 'textarea', required: true }
        ]
      }
    ]
  }
];

const steps = ['Catégorie', 'Type', 'Informations', 'Confirmation'];

export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState(null);
  const [form, setForm] = useState({});
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Sélection catégorie
  const handleSelectCategory = (cat) => {
    setSelectedCategory(cat);
    setSelectedType(null);
    setForm({});
    setActiveStep(1);
    setError("");
  };
  // Sélection type
  const handleSelectType = (type) => {
    setSelectedType(type);
    setForm({});
    setActiveStep(2);
    setError("");
  };
  // Retour
  const handleBack = () => {
    if (activeStep === 1) {
      setSelectedCategory(null);
      setActiveStep(0);
    } else if (activeStep === 2) {
      setSelectedType(null);
      setActiveStep(1);
    } else if (activeStep === 3) {
      setActiveStep(2);
    }
    setError("");
  };
  // Formulaire dynamique
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  // Validation étape
  const validateStep = () => {
    if (activeStep === 0 && !selectedCategory) {
      setError('Veuillez choisir une catégorie.');
      return false;
    }
    if (activeStep === 1 && !selectedType) {
      setError('Veuillez choisir un type de signalement.');
      return false;
    }
    if (activeStep === 2 && selectedType) {
      for (const field of selectedType.form) {
        if (field.required && !form[field.name]) {
          setError(`Veuillez renseigner le champ : ${field.label}`);
          return false;
        }
      }
    }
    setError("");
    return true;
  };
  // Suivant
  const handleNext = () => {
    if (!validateStep()) return;
    setActiveStep((prev) => prev + 1);
  };
  // Soumission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateStep()) return;
    setSuccess("Signalement transmis ! Merci pour votre vigilance.");
    setActiveStep(3);
  };

  // Affichage dynamique par étape
  const renderStepContent = () => {
    if (activeStep === 0) {
      // Catégories
      return (
        <Box>
          <Typography variant="h6" sx={{ mb: 2, textAlign: 'center' }}>Choisissez une catégorie</Typography>
          <Grid container spacing={3} justifyContent="center">
            {categories.map((cat) => (
              <Grid item xs={12} sm={6} md={4} key={cat.id}>
                <Card sx={{ cursor: 'pointer', border: selectedCategory?.id === cat.id ? '2px solid #1976d2' : '2px solid transparent', borderRadius: 4, boxShadow: 3 }} onClick={() => handleSelectCategory(cat)}>
                  <CardMedia component="img" height="120" image={cat.image} alt={cat.label} sx={{ objectFit: 'cover', borderRadius: 2 }} />
                  <CardContent>
                    <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 1, textAlign: 'center' }}>{cat.label}</Typography>
                    <Typography variant="body2" sx={{ color: '#374151', textAlign: 'center' }}>{cat.description}</Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      );
    }
    if (activeStep === 1 && selectedCategory) {
      // Types de la catégorie
      return (
        <Box>
          <Typography variant="h6" sx={{ mb: 2, textAlign: 'center' }}>Quel type de signalement&nbsp;?</Typography>
          <Grid container spacing={3} justifyContent="center">
            {selectedCategory.types.map((type) => (
              <Grid item xs={12} key={type.id}>
                <Card sx={{ cursor: 'pointer', border: selectedType?.id === type.id ? '2px solid #1976d2' : '2px solid transparent', borderRadius: 4, boxShadow: 2, mb: 2 }} onClick={() => handleSelectType(type)}>
                  <CardContent>
                    <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 1 }}>{type.label}</Typography>
                    <Typography variant="body2" sx={{ color: '#374151' }}>{type.description}</Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
            <Button onClick={handleBack} sx={{ borderRadius: 2, mr: 2 }}>Retour</Button>
            {selectedType && (
              <Button variant="contained" color="primary" onClick={handleNext} sx={{ borderRadius: 2, fontWeight: 600, px: 4 }}>
                Suivant
              </Button>
            )}
          </Box>
        </Box>
      );
    }
    if (activeStep === 2 && selectedType) {
      // Formulaire dynamique
      return (
        <Box component="form" onSubmit={handleSubmit}>
          <Typography variant="h6" sx={{ mb: 2, textAlign: 'center' }}>Formulaire&nbsp;: {selectedType.label}</Typography>
          <Grid container spacing={2}>
            {selectedType.form.map((field) => (
              <Grid item xs={12} sm={6} key={field.name}>
                {field.type === 'textarea' ? (
                  <TextField
                    label={field.label}
                    name={field.name}
                    value={form[field.name] || ''}
                    onChange={handleChange}
                    fullWidth
                    required={field.required}
                    multiline
                    rows={4}
                  />
                ) : (
                  <TextField
                    label={field.label}
                    name={field.name}
                    value={form[field.name] || ''}
                    onChange={handleChange}
                    fullWidth
                    required={field.required}
                    type={field.type}
                  />
                )}
              </Grid>
            ))}
          </Grid>
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
            <Button onClick={handleBack} sx={{ borderRadius: 2, mr: 2 }}>Retour</Button>
            <Button variant="contained" color="primary" type="submit" sx={{ borderRadius: 2, fontWeight: 600, px: 4 }}>
              Suivant
            </Button>
          </Box>
        </Box>
      );
    }
    if (activeStep === 3 && selectedType) {
      // Récapitulatif
      return (
        <Box>
          <Typography variant="h6" sx={{ mb: 2, textAlign: 'center' }}>Récapitulatif du signalement</Typography>
          <Paper elevation={2} sx={{ p: 3, borderRadius: 3, mb: 2, maxWidth: 600, mx: 'auto' }}>
            <Typography variant="subtitle1"><b>Catégorie :</b> {selectedCategory.label}</Typography>
            <Typography variant="subtitle1"><b>Type :</b> {selectedType.label}</Typography>
            {selectedType.form.map((field) => (
              <Typography key={field.name} variant="subtitle1"><b>{field.label} :</b> {form[field.name]}</Typography>
            ))}
          </Paper>
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mt: 2 }}>
            <Button variant="outlined" color="primary" sx={{ borderRadius: 2, fontWeight: 600 }} onClick={() => window.print()}>
              Imprimer
            </Button>
            <Button variant="contained" color="primary" sx={{ borderRadius: 2, fontWeight: 600 }} onClick={() => {
              const blob = new Blob([
                `Catégorie: ${selectedCategory.label}\nType: ${selectedType.label}\n` + selectedType.form.map(f => `${f.label}: ${form[f.name]}`).join('\n')
              ], { type: 'text/plain' });
              const url = URL.createObjectURL(blob);
              const a = document.createElement('a');
              a.href = url;
              a.download = 'signalement.txt';
              a.click();
              URL.revokeObjectURL(url);
            }}>
              Télécharger
            </Button>
          </Box>
        </Box>
      );
    }
    return null;
  };

  return (
    <Box sx={{ minHeight: '100vh', background: '#f8fafc', py: 6 }}>
      <Container maxWidth="md">
        <Typography variant="h4" sx={{ fontWeight: 800, color: '#1a237e', mb: 4, textAlign: 'center' }}>
          Signaler une fraude ou un message suspect
        </Typography>
        <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
        <Paper elevation={6} sx={{ p: 4, borderRadius: 4, background: '#fff', boxShadow: '0 4px 24px 0 rgba(26,35,126,0.10)' }}>
          {renderStepContent()}
        </Paper>
      </Container>
    </Box>
  );
}
