import React, { useState } from 'react';
import { Box, Container, Paper, Typography, Grid, Button, Alert, TextField } from '@mui/material';

// Informations pratiques à afficher en cards
const infosPratiques = [
  {
    title: "Messages et appels indésirables, kesako?",
    description: "On entend par messages et appels indésirables les communications non sollicitées à des fins publicitaires, commerciales ou malveillantes...",
    link: "https://www.33700.fr/informations-pratiques/messages-et-appels-indesirables-kesaco/",
    image: "/images/diomaye2.jpg"
  },
  {
    title: "Pourquoi signaler un SMS ou appel indésirable ?",
    description: "Plus il y a de signalements, plus la lutte contre les messages et appels indésirables est efficace.",
    link: "https://www.33700.fr/informations-pratiques/pourquoi-signaler-un-sms-ou-appel-indesirable-au-33700/",
    image: "/images/diomaye3.jpg"
  },
  {
    title: "Signaler un message SMS indésirable",
    description: "Vous venez de recevoir un SMS suspect ? Soyez vigilant, il s'agit peut-être d'un spam!",
    link: "https://www.33700.fr/informations-pratiques/signaler-un-message-sms-indesirable/",
    image: "/images/pngtree-button-senegal-flag-vector-template-design-png-image_859977.png"
  },
  {
    title: "Que devient mon signalement?",
    description: "Les signalements sont envoyés chaque jour aux opérateurs de télécommunication.",
    link: "https://www.33700.fr/informations-pratiques/signaler-un-message-sms-indesirable-dupliquer-7307/",
    image: "/images/Jubenti.jpg"
  },
  {
    title: "Signaler un appel indésirable",
    description: "Un appel qui vous incite à rappeler un numéro surtaxé ou étranger est souvent un spam téléphonique.",
    link: "https://www.33700.fr/informations-pratiques/signaler-un-appel-indesirable/",
    image: "/images/flag-map-of-senegal-logo-png_seeklogo-312017.png"
  },
  {
    title: "Comment se protéger des tentatives de fraudes?",
    description: "En cas de suspicion de spam, ne suivez pas les instructions du message. Soyez vigilant!",
    link: "https://www.33700.fr/informations-pratiques/signaler-un-message-sms-indesirable-copy/",
    image: "/images/pngtree-button-senegal-flag-vector-template-design-png-image_859977.jpg"
  }
];

export default function SignalementPage() {
  const [form, setForm] = useState({});
  const [files, setFiles] = useState({});
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Gestion du formulaire
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  const handleFileChange = (e, name) => {
    setFiles({ ...files, [name]: e.target.files });
  };

  // Validation simple
  const validateStep = () => {
    const fields = [
      { name: 'nom', label: 'Votre nom', required: true },
      { name: 'email', label: 'Votre email', required: true },
      { name: 'telephone', label: 'Votre téléphone', required: true },
      { name: 'description', label: 'Description', required: true }
    ];
    for (const field of fields) {
      if (field.required && !form[field.name]) {
        setError(`Veuillez renseigner le champ : ${field.label}`);
        return false;
      }
    }
    setError("");
    return true;
  };

  // Soumission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateStep()) return;
    setSuccess("Signalement transmis ! Merci pour votre vigilance.");
  };

  return (
    <Box sx={{ minHeight: '100vh', background: '#f8fafc', py: 6 }}>
      <Container maxWidth="md">
        <Typography variant="h4" sx={{ fontWeight: 800, color: '#1a237e', mb: 4, textAlign: 'center' }}>
          Informations pratiques
        </Typography>
        <Grid container spacing={3}>
          {infosPratiques.map((info, idx) => (
            <Grid item xs={12} sm={6} md={4} key={idx}>
              <Paper elevation={6} sx={{ p: 3, borderRadius: 4, height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', background: '#fff', boxShadow: '0 4px 24px 0 rgba(26,35,126,0.10)' }}>
                <img src={info.image} alt={info.title} style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 16, marginBottom: 16, boxShadow: '0 2px 8px #e3eafc' }} />
                <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 1, textAlign: 'center' }}>{info.title}</Typography>
                <Typography variant="body2" sx={{ color: '#374151', mb: 2, textAlign: 'center' }}>{info.description}</Typography>
                <Button variant="outlined" color="primary" href={info.link} target="_blank" sx={{ borderRadius: 2, fontWeight: 600, px: 2, mt: 'auto' }}>
                  En savoir plus
                </Button>
              </Paper>
            </Grid>
          ))}
        </Grid>
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6" sx={{ mb: 2 }}>Formulaire de signalement</Typography>
          {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
          {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
          <form onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Votre nom"
                  name="nom"
                  value={form.nom || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Votre email"
                  name="email"
                  value={form.email || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                  type="email"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Votre téléphone"
                  name="telephone"
                  value={form.telephone || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                  type="tel"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Description"
                  name="description"
                  value={form.description || ''}
                  onChange={handleChange}
                  fullWidth
                  required
                  multiline
                  rows={4}
                />
              </Grid>
              <Grid item xs={12}>
                <Button variant="contained" color="primary" type="submit" sx={{ borderRadius: 2, fontWeight: 600, px: 4 }}>
                  Transmettre le signalement
                </Button>
              </Grid>
            </Grid>
          </form>
        </Box>
      </Container>
    </Box>
  );
}
