import React, { useState } from "react";
import { Container, Box, Typography, Paper, Button, Grid, TextField, MenuItem, Stepper, Step, StepLabel, Alert } from "@mui/material";

const categories = [
  {
    label: "Atteintes graves aux personnes",
    icon: "üõë",
    types: [
      "Mise en danger de la vie (suicide, appel √† l‚Äôautomutilation)",
      "Menace ou apologie d'acte terroriste",
      "Menace ou incitation √† la violence",
      "Torture ou barbarie (images, vid√©os)",
      "Incitation √† commettre des infractions",
      "P√©dophilie / corruption de mineur",
      "Incitation √† la haine ou √† la discrimination",
      "Traite des √™tres humains"
    ]
  },
  {
    label: "Infractions √©conomiques & num√©riques",
    icon: "üßæ",
    types: [
      "Escroquerie en ligne (phishing, faux sites, etc.)",
      "Arnaque par SMS ou email (hame√ßonnage, code OTP)",
      "Faux documents (carte d'identit√©, dipl√¥me, contrat...)",
      "Usurpation d'identit√©",
      "Trafic illicite (stup√©fiants, armes...)",
      "Fraude bancaire ou carte bancaire"
    ]
  },
  {
    label: "Espace public et s√©curit√©",
    icon: "üåç",
    types: [
      "Accident sur voie publique (v√©hicule, pi√©ton, v√©lo...)",
      "Danger sur la route (chauss√©e, signalisation d√©fectueuse)",
      "Agression ou vol dans la rue",
      "D√©gradation ou vandalisme urbain",
      "Occupation ill√©gale de l‚Äôespace public",
      "Tapage, nuisances, troubles √† l‚Äôordre public"
    ]
  },
  {
    label: "Commerce et consommation",
    icon: "üõí",
    types: [
      "Pratique commerciale trompeuse (fausse promo, produit p√©rim√©)",
      "Vente ill√©gale (contre-fa√ßon, revente interdite)",
      "Refus de service discriminatoire",
      "Escroquerie au paiement (TPE truqu√©, CB scann√©e...)"
    ]
  },
  {
    label: "Animaux",
    icon: "üêæ",
    types: [
      "Actes de cruaut√© ou de maltraitance",
      "Abandon d‚Äôanimal domestique",
      "Chasse ou trafic ill√©gal d‚Äôesp√®ces prot√©g√©es"
    ]
  },
  {
    label: "Communication √©lectronique",
    icon: "üìû",
    types: [
      "Spam ou messages ind√©sirables",
      "SMS de fraude (arnaques CPF, colis, amendes)",
      "Appels frauduleux ou harc√®lement t√©l√©phonique",
      "Email suspect (tentative de phishing, ran√ßongiciel)"
    ]
  },
  {
    label: "Autres",
    icon: "üì¶",
    types: [
      "Contenu choquant ou inappropri√© en ligne",
      "Usurpation de profil ou faux compte sur r√©seaux sociaux",
      "Contrefa√ßon de marque ou produit"
    ]
  }
];

const steps = [
  "Cat√©gorie",
  "Type",
  "Informations",
  "Description",
  "Confirmation"
];

export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState("");
  const [form, setForm] = useState({
    motif: "",
    nom: "",
    prenom: "",
    email: "",
    telephone: "",
    description: ""
  });
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  const handleCategory = (cat) => {
    setSelectedCategory(cat);
    setSelectedType("");
    setForm({ ...form, motif: "" });
  };
  const handleType = (type) => {
    setSelectedType(type);
    setForm({ ...form, motif: type });
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleNext = () => {
    if (activeStep === 0 && !selectedType) {
      setError("Veuillez s√©lectionner un type de signalement.");
      return;
    }
    if (activeStep === 1 && (!form.nom || !form.prenom || !form.email)) {
      setError("Veuillez remplir tous les champs personnels obligatoires.");
      return;
    }
    if (activeStep === 2 && !form.description) {
      setError("Merci de d√©crire pr√©cis√©ment le signalement.");
      return;
    }
    setError("");
    setActiveStep((s) => s + 1);
  };

  const handleBack = () => {
    setError("");
    setActiveStep((s) => s - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess("Votre signalement a bien √©t√© transmis. Merci pour votre vigilance.");
    setActiveStep(steps.length - 1);
  };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)', py: 6, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Container maxWidth="md">
        <Paper elevation={12} sx={{ borderRadius: 6, p: { xs: 2, sm: 6 }, boxShadow: 10, background: 'rgba(255,255,255,0.98)' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
            <img src="/images/jub.png" alt="Logo S√©n√©gal" style={{ width: 90, marginRight: 32, borderRadius: '50%' }} />
            <Typography variant="h3" sx={{ fontWeight: 800, color: '#1a237e' }}>
              Signaler un contenu ou une fraude
