import React, { useState } from "react";
import { Container, Box, Typography, Paper, Button, Grid, TextField, MenuItem, Stepper, Step, StepLabel, Alert } from "@mui/material";

const categories = [
  {
    label: "Atteintes graves aux personnes",
    icon: "🛑",
    types: [
      "Mise en danger de la vie (suicide, appel à l’automutilation)",
      "Menace ou apologie d'acte terroriste",
      "Menace ou incitation à la violence",
      "Torture ou barbarie (images, vidéos)",
      "Incitation à commettre des infractions",
      "Pédophilie / corruption de mineur",
      "Incitation à la haine ou à la discrimination",
      "Traite des êtres humains"
    ]
  },
  {
    label: "Infractions économiques & numériques",
    icon: "🧾",
    types: [
      "Escroquerie en ligne (phishing, faux sites, etc.)",
      "Arnaque par SMS ou email (hameçonnage, code OTP)",
      "Faux documents (carte d'identité, diplôme, contrat...)",
      "Usurpation d'identité",
      "Trafic illicite (stupéfiants, armes...)",
      "Fraude bancaire ou carte bancaire"
    ]
  },
  {
    label: "Espace public et sécurité",
    icon: "🌍",
    types: [
      "Accident sur voie publique (véhicule, piéton, vélo...)",
      "Danger sur la route (chaussée, signalisation défectueuse)",
      "Agression ou vol dans la rue",
      "Dégradation ou vandalisme urbain",
      "Occupation illégale de l’espace public",
      "Tapage, nuisances, troubles à l’ordre public"
    ]
  },
  {
    label: "Commerce et consommation",
    icon: "🛒",
    types: [
      "Pratique commerciale trompeuse (fausse promo, produit périmé)",
      "Vente illégale (contre-façon, revente interdite)",
      "Refus de service discriminatoire",
      "Escroquerie au paiement (TPE truqué, CB scannée...)"
    ]
  },
  {
    label: "Animaux",
    icon: "🐾",
    types: [
      "Actes de cruauté ou de maltraitance",
      "Abandon d’animal domestique",
      "Chasse ou trafic illégal d’espèces protégées"
    ]
  },
  {
    label: "Communication électronique",
    icon: "📞",
    types: [
      "Spam ou messages indésirables",
      "SMS de fraude (arnaques CPF, colis, amendes)",
      "Appels frauduleux ou harcèlement téléphonique",
      "Email suspect (tentative de phishing, rançongiciel)"
    ]
  },
  {
    label: "Autres",
    icon: "📦",
    types: [
      "Contenu choquant ou inapproprié en ligne",
      "Usurpation de profil ou faux compte sur réseaux sociaux",
      "Contrefaçon de marque ou produit"
    ]
  }
];

const steps = [
  "Catégorie",
  "Type",
  "Informations",
  "Description",
  "Confirmation"
];

export default function SignalementPage() {
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState("");
  const [form, setForm] = useState({
    motif: "",
    nom: "",
    prenom: "",
    email: "",
    telephone: "",
    description: ""
  });
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  const handleCategory = (cat) => {
    setSelectedCategory(cat);
    setSelectedType("");
    setForm({ ...form, motif: "" });
  };
  const handleType = (type) => {
    setSelectedType(type);
    setForm({ ...form, motif: type });
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleNext = () => {
    if (activeStep === 0 && !selectedType) {
      setError("Veuillez sélectionner un type de signalement.");
      return;
    }
    if (activeStep === 1 && (!form.nom || !form.prenom || !form.email)) {
      setError("Veuillez remplir tous les champs personnels obligatoires.");
      return;
    }
    if (activeStep === 2 && !form.description) {
      setError("Merci de décrire précisément le signalement.");
      return;
    }
    setError("");
    setActiveStep((s) => s + 1);
  };

  const handleBack = () => {
    setError("");
    setActiveStep((s) => s - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess("Votre signalement a bien été transmis. Merci pour votre vigilance.");
    setActiveStep(steps.length - 1);
  };

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)', py: 6, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Container maxWidth="md">
        <Paper elevation={12} sx={{ borderRadius: 6, p: { xs: 2, sm: 6 }, boxShadow: 10, background: 'rgba(255,255,255,0.98)' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
            <img src="/images/jub.png" alt="Logo Sénégal" style={{ width: 90, marginRight: 32, borderRadius: '50%' }} />
            <Typography variant="h3" sx={{ fontWeight: 800, color: '#1a237e' }}>
              Signaler un contenu ou une fraude
            </Typography>
          </Box>
          <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
            <Step><StepLabel>Catégorie</StepLabel></Step>
            <Step><StepLabel>Type</StepLabel></Step>
            <Step><StepLabel>Informations</StepLabel></Step>
            <Step><StepLabel>Description</StepLabel></Step>
            <Step><StepLabel>Confirmation</StepLabel></Step>
          </Stepper>
          {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
          {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
          <form>
            {activeStep === 0 && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>Choisissez une catégorie de signalement :</Typography>
                <Grid container spacing={2}>
                  {categories.map((cat) => (
                    <Grid item xs={12} sm={6} md={4} key={cat.label}>
                      <Paper elevation={selectedCategory === cat ? 8 : 2} sx={{ p: 3, textAlign: 'center', cursor: 'pointer', border: selectedCategory === cat ? '2px solid #1976d2' : '1px solid #c3cfe2', background: selectedCategory === cat ? '#e3eafc' : '#fff', transition: 'all 0.2s' }} onClick={() => {
                        setSelectedCategory(cat);
                        setTimeout(() => {
                          window.location.href = "/type-signalement";
                        }, 200);
                        localStorage.setItem("selectedCategory", JSON.stringify(cat));
                      }}>
                        <Typography variant="h3" sx={{ mb: 1 }}>{cat.icon}</Typography>
                        <Typography variant="subtitle1" sx={{ fontWeight: 700 }}>{cat.label}</Typography>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            )}
            {activeStep === 1 && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>Vos informations personnelles</Typography>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <TextField label="Nom" name="nom" value={form.nom} onChange={handleChange} fullWidth required />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField label="Prénom" name="prenom" value={form.prenom} onChange={handleChange} fullWidth required />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField label="Email" name="email" value={form.email} onChange={handleChange} fullWidth required type="email" />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField label="Téléphone (optionnel)" name="telephone" value={form.telephone} onChange={handleChange} fullWidth />
                  </Grid>
                </Grid>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                  <Button variant="outlined" onClick={handleBack} sx={{ borderRadius: 2 }}>Précédent</Button>
                  <Button variant="contained" color="primary" onClick={handleNext} sx={{ borderRadius: 2 }}>Suivant</Button>
                </Box>
              </Box>
            )}
            {activeStep === 2 && (
              <Box>
                <Typography variant="h6" sx={{ mb: 2 }}>Décrivez précisément le contenu ou la fraude à signaler</Typography>
                <TextField
                  label="Description détaillée"
                  name="description"
                  value={form.description}
                  onChange={handleChange}
                  fullWidth
                  required
                  multiline
                  rows={5}
                  sx={{ mb: 3 }}
                />
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Button variant="outlined" onClick={handleBack} sx={{ borderRadius: 2 }}>Précédent</Button>
                  <Button variant="contained" color="primary" type="submit" sx={{ borderRadius: 2 }}>Envoyer</Button>
                </Box>
              </Box>
            )}
            {activeStep === 3 && (
              <Box sx={{ textAlign: 'center', py: 6 }}>
                <Typography variant="h4" color="success.main" sx={{ mb: 2 }}>Merci pour votre signalement !</Typography>
                <Typography variant="body1">Votre demande a bien été prise en compte. Nos équipes vont l'étudier dans les plus brefs délais.</Typography>
              </Box>
            )}
          </form>
        </Paper>
      </Container>
    </Box>
  );
}
