import React, { useState } from "react";
import { Container, Box, Typography, TextField, Button, Paper, Alert, Grid, Link } from "@mui/material";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import ReCAPTCHA from "react-google-recaptcha";

const RECAPTCHA_SITE_KEY = "6LfkPVErAAAAAKQktfpXosjg03RMBhzdytxLCZSu";

export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [recaptchaToken, setRecaptchaToken] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    if (!username || !password) {
      setError("Veuillez remplir tous les champs.");
      return;
    }
    if (!recaptchaToken) {
      setError("Veuillez valider le captcha.");
      return;
    }
    try {
      const res = await axios.post("/api/auth/login", { username, password, recaptchaToken });
      localStorage.setItem("token", res.data.token);
      navigate("/fraudes");
    } catch (err) {
      setError(err.response?.data?.error || "Erreur lors de la connexion");
    }
  };

  return (
    <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)' }}>
      <Container maxWidth="xs">
        <Paper elevation={8} sx={{ p: 4, borderRadius: 4, boxShadow: 6 }}>
          <Typography variant="h4" align="center" gutterBottom sx={{ fontWeight: 700, color: 'primary.main' }}>
            Connexion
          </Typography>
          <Typography variant="body2" align="center" color="text.secondary" sx={{ mb: 3 }}>
            Connectez-vous à votre compte pour accéder à l'application
          </Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField label="Adresse email" variant="outlined" fullWidth required value={username} onChange={e => setUsername(e.target.value)} autoComplete="email" />
            <TextField label="Mot de passe" type="password" variant="outlined" fullWidth required value={password} onChange={e => setPassword(e.target.value)} autoComplete="current-password" />
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 1 }}>
              <ReCAPTCHA sitekey={RECAPTCHA_SITE_KEY} onChange={token => setRecaptchaToken(token)} />
            </Box>
            <Button type="submit" variant="contained" color="primary" size="large" sx={{ mt: 2, fontWeight: 600 }}>
              Se connecter
            </Button>
          </Box>
          <Grid container justifyContent="space-between" sx={{ mt: 2 }}>
            <Grid item>
              <Link href="/register" underline="hover" color="primary">
                Créer un compte
              </Link>
            </Grid>
            <Grid item>
              <Link href="/forgot-password" underline="hover" color="primary">
                Mot de passe oublié ?
              </Link>
            </Grid>
          </Grid>
          {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
        </Paper>
      </Container>
    </Box>
  );
}
