import React, { useState } from 'react';
import { Box, Container, Paper, Typography, Grid, Button, Alert, TextField, Stepper, Step, StepLabel, MobileStepper, Card, CardContent } from '@mui/material';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import PersonIcon from '@mui/icons-material/Person';
import GavelIcon from '@mui/icons-material/Gavel';
import PublicIcon from '@mui/icons-material/Public';
import ConstructionIcon from '@mui/icons-material/Construction';
import StorefrontIcon from '@mui/icons-material/Storefront';
import PetsIcon from '@mui/icons-material/Pets';
import SecurityIcon from '@mui/icons-material/Security';

// Palette bleu et blanc uniquement
const senegalColors = {
  blue: '#1976d2', // Bleu principal
  darkBlue: '#00008b', // Bleu foncé
  lightBlue: '#e3e8ff',
  white: '#fff',
};

// Nouvelle structure modulaire des catégories, types et formulaires
const categories = [
  {
    id: 'atteintes-personnes',
    label: 'Atteintes aux personnes',
    description: 'Signalements concernant la sécurité ou l’intégrité des personnes.',
    icon: <PersonIcon sx={{ fontSize: 48, color: '#1976d2' }} />,
    types: [
      {
        id: 'suicide-danger',
        label: 'Annonce de suicide / mise en danger',
        description: '',
        form: [
          { name: 'description', label: 'Description de la situation', type: 'textarea', required: true },
          { name: 'date', label: 'Date et heure de l’annonce ou observation', type: 'datetime-local', required: true },
          { name: 'plateforme', label: 'Plateforme ou contexte', type: 'select', required: true, options: ['Réseaux sociaux', 'Appel', 'Lettre', 'Autre'] },
          { name: 'preuve', label: 'Preuves (captures d’écran, vidéos, messages…)', type: 'file', required: false, multiple: true, maxSizeMB: 10, accept: ['image/*','video/*','application/pdf','application/msword','application/vnd.openxmlformats-officedocument.wordprocessingml.document'], help: 'Jusqu’à 10 Mo par fichier. Formats : images, vidéos, PDF, Word.' },
          { name: 'contact', label: 'Contact de la victime', type: 'contact', required: false }
        ]
      },
      {
        id: 'menace-mort',
        label: 'Menaces de mort',
        description: '',
        form: [
          { name: 'auteur', label: 'Auteur présumé (nom ou pseudo)', type: 'text', required: false },
          { name: 'contenu', label: 'Contenu de la menace', type: 'textarea', required: true },
          { name: 'support', label: 'Support', type: 'select', required: true, options: ['SMS', 'Message', 'Appel', 'Autre'] },
          { name: 'preuve', label: 'Preuves (capture, enregistrement…)', type: 'file', required: false, multiple: true },
          { name: 'date', label: 'Date et heure de l’incident', type: 'datetime-local', required: true }
        ]
      },
      {
        id: 'violences-physiques',
        label: 'Violences physiques',
        description: '',
        form: [
          { name: 'lieu', label: 'Lieu des faits', type: 'text', required: true },
          { name: 'description', label: 'Description des blessures', type: 'textarea', required: true },
          { name: 'temoins', label: 'Témoins éventuels', type: 'text', required: false },
          { name: 'preuve', label: 'Preuves (photos, certificats médicaux)', type: 'file', required: false, multiple: true },
          { name: 'date', label: 'Date et heure de l’agression', type: 'datetime-local', required: true }
        ]
      },
      {
        id: 'agressions-sexuelles',
        label: 'Agressions sexuelles',
        description: '',
        form: [
          { name: 'nature', label: "Nature de l'agression", type: 'text', required: true },
          { name: 'agresseur', label: "Identité présumée de l’agresseur (si connue)", type: 'text', required: false },
          { name: 'date-lieu', label: 'Date et lieu', type: 'text', required: true },
          { name: 'preuve', label: 'Témoins ou preuves (certificat, message, vidéo…)', type: 'file', required: false, multiple: true, maxSizeMB: 10, accept: ['image/*','video/*','application/pdf','application/msword','application/vnd.openxmlformats-officedocument.wordprocessingml.document'], help: 'Jusqu’à 10 Mo par fichier. Formats : images, vidéos, PDF, Word.' },
          { name: 'circonstances', label: 'Circonstances', type: 'textarea', required: false },
          { name: 'contact', label: 'Contact de la victime', type: 'contact', required: false }
        ]
      },
      {
        id: 'violences-conjugales',
        label: 'Violences conjugales',
        description: '',
        form: [
          { name: 'description', label: 'Description des faits', type: 'textarea', required: true },
          { name: 'type-violence', label: 'Type de violence', type: 'select', required: true, options: ['Verbale', 'Physique', 'Psychologique', 'Autre'] },
          { name: 'date-frequence', label: 'Date et fréquence des violences', type: 'text', required: true },
          { name: 'preuve', label: 'Preuves (photo, audio, vidéo)', type: 'file', required: false, multiple: true, maxSizeMB: 10, accept: ['image/*','video/*','application/pdf','application/msword','application/vnd.openxmlformats-officedocument.wordprocessingml.document'], help: 'Jusqu’à 10 Mo par fichier. Formats : images, vidéos, PDF, Word.' },
          { name: 'enfants', label: 'Enfants concernés ?', type: 'select', required: false, options: ['Oui', 'Non'] },
          { name: 'contact', label: 'Contact de la victime', type: 'contact', required: false }
        ]
      },
      {
        id: 'cyberharcelement',
        label: 'Cyberharcèlement personnel',
        description: '',
        form: [
          { name: 'plateforme', label: 'Plateforme utilisée', type: 'select', required: true, options: ['Réseaux sociaux', 'Email', 'Forum', 'Autre'] },
          { name: 'auteur', label: 'Auteur présumé (pseudo ou nom)', type: 'text', required: false },
          { name: 'description', label: 'Description des faits', type: 'textarea', required: true },
          { name: 'captures', label: 'Captures d’écran', type: 'file', required: false, multiple: true, maxSizeMB: 10, accept: ['image/*','video/*','application/pdf','application/msword','application/vnd.openxmlformats-officedocument.wordprocessingml.document'], help: 'Jusqu’à 10 Mo par fichier. Formats : images, vidéos, PDF, Word.' },
          { name: 'frequence', label: 'Fréquence et durée', type: 'text', required: false },
          { name: 'contact', label: 'Contact de la victime', type: 'contact', required: false }
        ]
      },
      {
